INSERT INTO Products (prod_id, prod_name, prod_desc, prod_prix_ttc, frs_id, family_id, appell_id, region_id, color_id, cont_id, prod_img, prod_actif) VALUES
("00020", "Aérateur de Vin Ludi-Vin + Filtre", "Accessoire permettant d’aérer un vin, équivalent à deux heures de carafage.", "36.8", "12", "10", "0", "0","0", "0", "aerateur_vin_ludivin.png", "1"),
("00022", "Carafe First Sweizel 750 ml", "Carafe à vin rouge d’une capacité de 750 ml.<br><br>Sa forme longue et evasée lui permet d'accueilllir les vins jeunes afin d'exprimer au mieux leurs arômes.", "63.3", "12", "10", "0", "39","0", "0", "carafe_first_sweizel_750_ml.png", "1"),
("00023", "Carafe Freshness 1L", "Carafe à eau, à vin rosé et blanc d’une capacité de 1 L.<br><br>Sa forme longue et verticale lui permet d'accueillir les vins blancs et rosés.", "48.6", "11", "10", "0", "39","0", "0", "carafe_freshness_chef_sommelier_1_l.png", "1"),
("00026", "Giftpack 1 Pompe + 2 Bouchons Vacuvin", "Accessoire permettant de vider l’air à l’intérieur d’une bouteille de vin afin de le conserver plus longtemps.<br>Contient 1 pompe et 2 bouchons.", "14.9", "12", "10", "0", "0","0", "0", "vacuvin_pompe_2_bouchons.png", "1"),
("00028", "Marques-Verres  X 8", "Accessoire permettant de différencier votre verre lors d’une soirée.", "7.1", "12", "10", "0", "0","0", "0", "marques_verres.png", "1"),
("00029", "Mini-Bouchon Pompe Vide Air", "Accessoire permettant de vider l’air dans une bouteille de vin afin de mieux conserver le vin.", "4.9", "42", "10", "0", "0","0", "0", "mini_bouchon_pompe_vide_air.png", "1"),
("00030", "Pierres à Whisky  X 8", "Accessoire permettant de rafraîchir votre whisky ou spiritueux sans mettre de l’eau et ainsi réduire la qualité de votre boisson.", "19.9", "12", "10", "0", "0","0", "0", "pierres_a_whisky.png", "1"),
("00031", "Pince à Champagne", "Accessoire permettant de facilité l’ouverture d’un champagne ou d’un vin effervescent.", "19.2", "34", "10", "0", "0","0", "0", "pince_a_champagne.png", "1"),
("00032", "Récupérateur de Bouchon", "Accessoire permettant de facilité la récupération d’un bouchon tombé dans la bouteille à l’ouverture.", "9.93", "12", "10", "0", "0","0", "0", "recuperateur_bouchon.png", "1"),
("00033", "Sac Refroidisseur en Gel", "Accessoire permettant de garder frais votre bouteille de vin ou de champagne.", "4.85", "12", "10", "0", "0","0", "0", "sac_refroidisseur_gel.png", "1"),
("00034", "Set de 2 Bouchons pour pompe Vacuvin", "Recharge de bouchons pour l’accessoire vacuvin (pompe vide air).", "4.95", "42", "10", "0", "0","0", "0", "set_2_bouchons_a_pompe_vacuvin.png", "1"),
("00040", "Tire-bouchon électrique Argent", "Accessoire éléctrique permettant de déboucher une bouteille de vin. Fonctionne sur batterie rechargeable.", "61.5", "42", "10", "0", "0","0", "0", "tire_bouchon_electrique_argent_ludivin.png", "1"),
("00044", "Tire-bouchon Trigger Lm350 + Coupe Capsule Platinium", "Accessoire permettant de déboucher une bouteille de vin.<br><br>Tire-Bouchon professionnel ayant une haute resistance à la pression. Il est agréable au toucher et est particulièrement résistant aux chocs.", "157", "12", "10", "0", "0","0", "0", "tire_bouchon_screwpul_lm_350p.png", "1"),
("00049", "Bas Armagnac Carafe Aramis Intemporel 12 ans", "Un Armagnac 12 ans d’une incroyable puissance et complexité aromatique. Idéal en digestif ou sur des fraises gariguettes.<br><br>Provenance : France (Landes et Lot-et-Garonne)<br><br>Cépages : Ugni blanc, Folle blanche, Baco et Colombard<br><br>Vieillissement : assemblage de 15 eaux-de-vie de 12 à 20 ans vieillies en fûts de chêne.<br><br>Dégustation : Robe ambrée foncée ; Nez de fruits confits, vanille, pruneaux et d’épices ; Bouche racée, puissante et riche. Son rancio typique des vieux bas armagnacs est net et persistant.<br><br>Le Château de Laubade est une propriété agricole et viticole située à Sorbets, dans le Gers, au cœur du terroir le plus noble du Bas Armagnac. Bâti en 1870, le domaine de 105 hectares d’un seul tenant appartient à la Famille Lesgourgues depuis trois générations. En quête d’excellence, c’est actuellement Denis, épaulé par sa sœur Jeanne et son frère Arnaud, qui poursuit les efforts visant à créer des armagnacs des plus singuliers.", "70.8", "28", "7", "0", "4","0", "15", "armagnac_laubade_intemportel_12_ans.png", "1"),
("00050", "Bas Armagnac Carafe Eclat VSOP Laubade", "Un Armagnac VSOP riche et élégant. Idéal en apéritif ou en digestif.<br><br>Provenance : France (Landes et Lot-et-Garonne)<br><br>Cépages : Ugni blanc et Folle blanche<br><br>Vieillissement : 6 à 12 ans en fûts de chêne.<br><br>Dégustation : Robe jaune doré ; Nez de fruits mûrs, vanille, prune et d’agrumes ; Bouche suave, délicat d’une belle longueur sur des notes patissières.<br><br>Le Château de Laubade est une propriété agricole et viticole située à Sorbets, dans le Gers, au cœur du terroir le plus noble du Bas Armagnac. Bâti en 1870, le domaine de 105 hectares d’un seul tenant appartient à la Famille Lesgourgues depuis trois générations. En quête d’excellence, c’est actuellement Denis, épaulé par sa sœur Jeanne et son frère Arnaud, qui poursuit les efforts visant à créer des armagnacs des plus singuliers.", "49.6", "28", "7", "0", "4","0", "15", "armagnac_laubade_eclat_vsop.png", "1"),
("00053", "Bas Armagnac Laubade N°5 Carafe Intemporelle", "Un Armagnac Hors d’Age élégant, fin et d’une grande maturité. Parfait en digestif ou sur un dessert aux fruits frais.<br><br>Provenance : France (Landes et Lot-et-Garonne)<br><br>Cépages : Ugni blanc, Folle blanche, Baco et Colombard<br><br>Vieillissement : 30 à 40 ans en fûts de chêne.<br><br>Dégustation : Robe acajou ; Nez intense de pruneau, figue, cacao, pruneau, torréfaction ; Bouche souple, boisée, riche d’une finale exceptionnelle.<br><br>Le Château de Laubade est une propriété agricole et viticole située à Sorbets, dans le Gers, au cœur du terroir le plus noble du Bas Armagnac. Bâti en 1870, le domaine de 105 hectares d’un seul tenant appartient à la Famille Lesgourgues depuis trois générations. En quête d’excellence, c’est actuellement Denis, épaulé par sa sœur Jeanne et son frère Arnaud, qui poursuit les efforts visant à créer des armagnacs des plus singuliers.", "105.8", "28", "7", "0", "4","0", "0", "armagnac_laubade_intemporel_n5.png", "1"),
("00054", "Bas Armagnac Laubade VS Carafe Signature", "Un Armagnac VS fruité et grillé/toasté. A déguster pur en apéritif ou à utiliser pour comme base de cocktail.<br><br>Provenance : France (Landes et Lot-et-Garonne)<br><br>Cépages : Ugni blanc, Colombard, Baco et Folle blanche<br><br>Vieillissement : 3 ans en fûts de chêne.<br><br>Dégustation : Robe dorée ; Nez intense et riche aux arômes de pêche, d’abricot et de prune ; Bouche souple et puissante aux notes épicées, vanillées et réglissées.<br><br>Le Château de Laubade est une propriété agricole et viticole située à Sorbets, dans le Gers, au cœur du terroir le plus noble du Bas Armagnac. Bâti en 1870, le domaine de 105 hectares d’un seul tenant appartient à la Famille Lesgourgues depuis trois générations. En quête d’excellence, c’est actuellement Denis, épaulé par sa sœur Jeanne et son frère Arnaud, qui poursuit les efforts visant à créer des armagnacs des plus singuliers.", "31.75", "28", "7", "0", "4","0", "15", "armagnac_laubade_signature_vs.png", "1"),
("00059", "Calvados Pays d'Auge Roger Groult 3 ans", "Un Calvados 3ans à apprécier dans sa plus simple expression, idéal à déguster à l'apéritif, pur ou en cocktail.<br><br>Provenance : France (Normandie)<br><br>Vieillissement : double distillation au feu de bois + vieillissement de 3 ans en vieux fûts de chêne français.<br><br>Dégustation : Robe orange clair ; Nez d’arômes intense de pomme fraîche acidulée ; Bouche oncteuse et douce, idéal en cocktail, digestif, givré ou à température ambiante.<br><br>Situé sur les hauteur du Pays d’Auge à Saint Cyr du Ronceray, c’est en 1860 que Pierre Groult commença à distiller son cidre dans le but d’obtenir sa prope eau-de-vie. <br>Distillerie familiale, les traditions sont perpétuées car aujourd’hui ce sont Estelle, Charlotte et Jean-Roger qui gèrent la distillerie. Tout en maintenant certaines méthodes de production ancestrales telles la double distillation au feu de bois ou l'assemblage perpétuel.", "32.8", "28", "7", "0", "11","0", "15", "calvados_roger_groult_3_ans.png", "1"),
("00060", "Calvados Pays d'Auge Roger Groult 8 ans", "Un Calvados 8 ans rond et gourmand. Idéal sur un plateau de fromage.<br><br>Provenance : France (Normandie)<br><br>Vieillissement : double distillation au feu de bois + vieillissement de 8 ans en vieux fûts de chêne français.<br><br>Dégustation : Robe orange clair ; Nez d’arômes intense de pomme légèrement cuite, de vanille et canelle ; Bouche gourmande, aromatique et longue. Idéal en digestif, givré ou avec un plateau de fromages.<br><br>Situé sur les hauteur du Pays d’Auge à Saint Cyr du Ronceray, c’est en 1860 que Pierre Groult commença à distiller son cidre dans le but d’obtenir sa prope eau-de-vie. <br>Distillerie familiale, les traditions sont perpétuées car aujourd’hui ce sont Estelle, Charlotte et Jean-Roger qui gèrent la distillerie. Tout en maintenant certaines méthodes de production ancestrales telles la double distillation au feu de bois ou l'assemblage perpétuel.", "52.45", "28", "7", "0", "11","0", "15", "calvados_roger_groult_8_ans.png", "1"),
("00073", "Gin 42° Mac Malden", "Un Gin bourguignon qui dévoile toute sa fraîcheur pur sur glace ou dans un gin tonic ou dans un cocktail que vous aurez imaginé.<br><br>Provenance : France (Bourgogne)<br><br>Vieillissement : Macération de baies de genièvre de 3 origines dans un mélange eau et alcool pendant plusieurs jours avant distillation. La bouteille est en aluminium dont le faible poids diminue le bilan carbone et est recyclable à l’infini.<br><br>Dégustation : Robe claire ; Nez de genièvre, de poivre et d’agrumes ; Bouche tendre, suave et grasse. A servir sur glace ou en cokctails.<br><br>Malden Spirits est une aventure qui, depuis sa naissance, tutoie l’innovation, la disruptivité, la différenciation : elle est née au cœur de la Bourgogne, au cœur du vignoble bourguignon, et a été imaginée par l’équipe du Domaine Maldant-Pauvelot.", "42", "23", "7", "0", "21","0", "15", "gin_mac_malden.png", "1"),
("00080", "Liqueur Prunelle de Bourgogne Cartron 40°", "Une liqueur de prunelle gourmande et généreuse. Idéal sur glace ou en cocktail.<br><br>Provenance : France (Bourgogne)<br><br>Dégustation : Robe dorée ; Nez aromatique sur le fruit à noyau ; Bouche généréeuse, ronde d’une sucrosité discrète aux notes de fruits à noyaux. Superbe longueur.<br><br>La Bourgogne fait partie intégrante de l’histoire de la famille Cartron depuis plus de deux siècles.<br>En effet, Jean Cartron s’installe en Bourgogne, à Argilly, à quelques kilomètres de Nuits-Saint-Georges. Il devient rapidement propriétaire terrien grâce à son mariage avec une Bourguignonne.<br>Par la suite, son petit-fils, Pierre Cartron, le père de Joseph, s’installe à Nuits-Saint-Georges au début des années 1870. C’est dans cette ville que Joseph créera la société éponyme en 1882 où elle est toujours ancrée.", "26.5", "24", "7", "0", "26","0", "15", "joseph_cartron_liqueur_prunelle.png", "1"),
("00101", "Rhum JM Vieux VSOP Martinique", "Un Rhum Agricole VSOP martiniquais boisé et torréfié. Idéal en apéritif, digestif ou sur un gâteau en chocolat.<br><br>Provenance : Martinique<br><br>Vieillissement : 3 ans en fûts neufs et en fûts d’ex-Bourbon + 1 an de finition en fûts de capacités et chauffes différentes<br><br>Dégustation : Robe ambrée ; Nez boisé, vanillé aux notes de tabac, d’épices et d’agrumes ; Bouche boisée, fumée, torréfiée et épicée.<br><br>Nichée au pied de la montagne Pelée dans un écrin luxuriant du Macouba à l’extrême-nord de la Martinique, la Distillerie J.M est reconnaissable à ses toits rouges. Considérées par la presse et les connaisseurs comme le meilleur rhum vieux du monde, les créations J.M ont une saveur unique. La richesse sauvage de ce terroir préservé, sa nature généreuse, son riche sol volcanique, son climat contrasté, son eau pure naturellement filtrée et sa canne à sucre ultra-fraîche donnent tout son caractère à ce AOC Rhum Agricole Martinique.", "59", "24", "7", "0", "34","0", "15", "rhum_jm_vsop.png", "1"),
("00118", "Whisky Charolais Mac Malden 12 ans 50 cl", "Un Whisky écossais blended fin, élégant et très subtil. Idéal en digestif ou sur une tarte aux fruits.<br><br>Provenance : Ecosse<br><br>Vieillissement : en fût de Bourbon et affiné en fût de Corton-Renardes (Bourgogne).<br><br>Dégustation : Robe ambrée ; Nez subtile et gourmand sur la mirabelle, la fraise et la noisette torréfiée ; Bouche ample, complexe sur des notes de gingembre, pamplemousse. Finale d’une grande finesse et longue.<br><br>Malden Spirits est une aventure qui, depuis sa naissance, tutoie l’innovation, la disruptivité, la différenciation : elle est née au cœur de la Bourgogne, au cœur du vignoble bourguignon, et a été imaginée par l’équipe du Domaine Maldant-Pauvelot.", "59", "23", "7", "0", "42","0", "13", "whisky_mac_malden_charolais.png", "1"),
("00123", "Whisky Froggy Mac Malden 70 cl", "Un Whisky écossais blended généreux et légèrement iodé. Idéal en apéritif ou sur une tarte aux fruits.<br><br>Provenance : Ecosse<br><br>Vieillissement : en fût de Bourbon<br><br>Dégustation : Robe or ; Nez vanillée et maltée ; Bouche gourmande, épicée et légèrement iodée. Finale saline et ronde.<br><br>Malden Spirits est une aventure qui, depuis sa naissance, tutoie l’innovation, la disruptivité, la différenciation : elle est née au cœur de la Bourgogne, au cœur du vignoble bourguignon, et a été imaginée par l’équipe du Domaine Maldant-Pauvelot.", "41", "23", "7", "0", "42","0", "15", "whisky_mac_malden_froggy.png", "1"),
("00134", "Whisky Johnny Hepp Single Malt", "Un Whisky français single malt généreux et élégant. Il pourra accompagner une langoustine ou un dessert aux fruits frais.<br><br>Provenance : France (Alsace)<br><br>Vieillissement : 18 mois en fûts de Bourbon et 18 mois en fûts de vins blancs de Bourgogne.<br><br>Dégustation : Robe ambrée acajou ; Nez floral et fruité aux notes d’agrumes et légèrement vanillé ; Bouche souple, boisée aux saveurs de vanille, caramel. Finale longue et épicée.<br><br>La Distillerie de Yannick Hepp est situé à Uberach, au cœur de cette partie de l'Alsace où les arbres fruitiers prospèrent. Et c’est depuis toujours qu’elle s’impose comme l’un des leaders de la production Française. Forte de son succès sur le réseau traditionnel, elle franchit un nouveau cap en 2022 avec le lancement de single malts de 8ans et de 11ans.", "49.4", "28", "7", "0", "42","0", "15", "whisky_hepp_johnny_hepp.png", "1"),
("00150", "Whisky White Bresse Mac Malden 50 cl", "Un Whisky écossais blended complexe et chaleureux. Parfait en apéritif, en digestif ou sur un plateau d’huitres.<br><br>Provenance : Ecosse<br><br>Vieillissement : en fût de Chardonnay (Bourgogne)<br><br>Dégustation : Robe jaune pâle ; Nez riche et concentré sur la poire, le miel ; Bouche gourmande aux notes de pâte d’amande, de chocolat, de citron confit. Finale lègerement saline, longue et fraîche.<br><br>Malden Spirits est une aventure qui, depuis sa naissance, tutoie l’innovation, la disruptivité, la différenciation : elle est née au cœur de la Bourgogne, au cœur du vignoble bourguignon, et a été imaginée par l’équipe du Domaine Maldant-Pauvelot.", "54", "23", "7", "0", "42","0", "13", "whisky_mac_malden_white_bresse.png", "1"),
("00161", "Bordeaux Dourthe n°1 Blanc", "Un Bordeaux blanc sec, vif et aromatique. Idéal sur un plateau de fruits de mer.<br><br>Encépagement : Sauvignon<br><br>Dégustation : Robe jaune pâle ; Nez aux notes d’agrumes (citron, pamplemousse) ; Bouche fraîche, tendue et minérale sur des arômes de citron.<br>Accord mets/vin : poissons, viande blanche.<br><br>La Maison Dourthe est de rassemblement de vignerons et à travers de ses Châteaux, la maison s’investit sur des sols variés et riches.<br>La Maison Dourthe produit ainsi de grands vins sincères, qui expriment librement toute la personnalité des terroirs de la région dans le respect de l’environnement, de sa biodiversité et des hommes, pour offrir au plus grand nombre des moments de plaisirs authentiques et partagés.", "9.95", "9", "2", "7", "8","2", "16", "dourthe_numero_un_blanc.png", "1"),
("00162", "Bordeaux Dourthe n°1 Rouge", "Un Bordeaux rouge léger et fruité. Idéal sur un faux filet grillée avec ses frites.<br><br>Encépagement : Merlot, Cabernet sauvignon, Cabernet franc<br><br>Dégustation : Robe rouge rubis ; Nez aux notes de fruits rouges et noirs ; Bouche ample sur des arômes de fruits rouges et de poivre noir.<br>Accord mets/vin : viande grillée, viande blanche.<br><br>La Maison Dourthe est de rassemblement de vignerons et à travers de ses Châteaux, la maison s’investit sur des sols variés et riches.<br>La Maison Dourthe produit ainsi de grands vins sincères, qui expriment librement toute la personnalité des terroirs de la région dans le respect de l’environnement, de sa biodiversité et des hommes, pour offrir au plus grand nombre des moments de plaisirs authentiques et partagés.", "11.45", "9", "1", "7", "8","8", "16", "dourthe_numero_un_rouge.png", "1"),
("00169", "Graves Château Haut Selve Blanc", "Un Graves blanc sec, rond et minérale. Idéal sur un plateau de frtuits mer.<br><br>Encépagement : Sauvignon Blanc, Sauvignon Gris, Sémillon<br><br>Dégustation : Robe jaune pâle ; Nez fin et fruité aux notes d'agrumes et fruits blancs ; Bouche ronde et équilibrée qui révèle des arômes d'agrumes (citron). Un vin blanc d'une belle minéralité avec une finale longue et fraîche.<br>Accord mets/vin : Fruits de mer, poissons grillés et fromages affinés.<br><br>Après avoir été détruit et abandonné, le Château Haut Selve est devenu l'unique création viticole du XXème sièvle en bordelais.<br>La propriété résulte d'une démarche esthétique et sensuelle. Chaque étape a été pensée comme une oeuvre, alliant contemporanéité et classicisme. La culture et l'art du vin marquent ici la singularité de cette propriété qui est reconnue pour être parmi les plus réputées de son appellation.", "16.2", "28", "2", "35", "8","2", "16", "chateau_haut_selve_graves_blanc.png", "1"),
("00170", "Graves Château Haut Selve Rouge", "Un Graves rouge puissant, charnue et boisé. Parfait sur une côtes de boeuf grillée.<br><br>Encépagement : Merlot, Cabernet sauvignon<br><br>Dégustation : Robe rouge brillante ; Nez de garrigue et d'épices ; Bouche ronde et aux notes de fruits noirs, un peu épicé (poivré). Des tannins fins et soyeux avec une finale longue et fruitée.<br>Accord mets/vin : viande rouge, gibier.<br><br>Existe en Magnum et en Jéroboam.<br><br>Après avoir été détruit et abandonné, le Château Haut Selve est devenu l'unique création viticole du XXème sièvle en bordelais.<br>La propriété résulte d'une démarche esthétique et sensuelle. Chaque étape a été pensée comme une oeuvre, alliant contemporanéité et classicisme. La culture et l'art du vin marquent ici la singularité de cette propriété qui est reconnue pour être parmi les plus réputées de son appellation.", "17.8", "28", "1", "35", "8","8", "16", "chateau_haut_selve_graves_rouge.png", "1"),
("00173", "Haut-Médoc Diane de Belgrave Rouge 2015", "Un Haut Médoc rouge gourmand et généreux. Parfait sur une daube de sanglier.<br><br>Encépagement : Merlot, Cabernet sauvignon<br><br>Dégustation : Robe rouge rubis ; Nez frais aux notes de fruits mûrs avec des touches poivrées et boisées ; Bouche charnue et ronde offre un bel équilibre entre le fruit, les tannins mûrs et un boisé fondu. Un vin riche avec une belle longueur en bouche.  <br><br>Accord mets/vin : viande rouge, gibier.<br><br>Grâce aux études menées sur la propriété pour comprendre le comportement de la vigne suivant la nature du sous-sol et de son alimentation en eau, l'équipe du Château Belgrave a acquis une connaissance parfaite du vignoble. Les travaux viticoles, associés à la protection raisonnée, ainsi que les vendanges ont été adaptés à cette cartographie. ", "21.7", "9", "1", "36", "8","8", "16", "chateau_belgrave_haut_medoc_diane_de_belgrave_rouge.png", "1"),
("00181", "Montagne St Emilion Château Teyssier Rouge 2018", "Un Montagne Saint Emilion (satellite de l’apellation Saint Emilion) rouge boisé, fruité et plein de finesse. Idéal sur un bœuf bourguignon.<br><br>Encépagement : Merlot, Cabernet franc<br><br>Dégustation : Robe rouge rubis ; Nez boisé aux notes de fruits rouges ; Bouche ronde et fruitée sur une finale longue et délicate.<br>Accord mets/vin : viande grillée ou en sauce, gibier.<br><br>Cet ancien vignoble, qui remonte au XVème siècle, est idéalement situé à l’un des points les plus hauts d’un tertre argilo-calcaire à quelques kilomètres au nord-est de Saint-Emilion.<br>Antoine Darquey, gérant depuis 1993 et son cousin Thomas Durand-Teyssier, Directeur d’Exploitation depuis 2007, représentent la quatrième génération à la tête de cette magnifique propriété.", "20.9", "9", "1", "52", "8","8", "16", "chateau_teyssier_montagne_saint_emilion_rouge.png", "1"),
("00186", "Pessac-Leognan Château La Garde Rouge", "Un Pessac Léognan généreux, complexe et fruité. Parfait sur un carré d’agneau grillé.<br><br>Encépagement : Merlot, Cabernet sauvignon<br><br>Dégustation : Robe rubis sombre ; Nez intense et complexe sur des notes d’épices et de fruits noirs ; Bouche ample, dense et délicate d’une grande fraicheur et aux arômes de fruits bien murs.<br>Accord mets/vin : viande rouge, gibier.<br><br>Grâce aux études menées sur la propriété pour comprendre le comportement de la vigne suivant la nature du sous-sol et de son alimentation en eau, l'équipe du Château Belgrave a acquis une connaissance parfaite du vignoble. Les travaux viticoles, associés à la protection raisonnée, ainsi que les vendanges ont été adaptés à cette cartographie. <br>Le vin exprime toute la finesse et la personnalité de ce grand terroir.", "36.3", "9", "1", "56", "8","8", "16", "chateau_la_garde_pessac_leognan_rouge.png", "1"),
("00192", "Puisseguin St Emilion Château Lanbersac Rouge", "Un Puisseguin Saint-Emilion (satellite de l’appellation Saint-Emilion) boisé, élégant et fruité. Parfait sur un faux filet sauce aux cèpes.<br><br>Encépagement : Merlot, Cabernet franc<br><br>Dégustation : Robe rouge rubis ; Nez boisé aux notes de fruits noirs ; Bouche ronde aux arômes de cerises et de caramel<br>Accord mets/vin : viande grillée, fromages corsés.<br><br>Le Château Lanbersac est une propriété de 22 hectares située au sud du plateau calcaire à Astéries, de l’appellation Puisseguin Saint-Émilion.<br>Les vignes de la propriété ne sont pas d’un seul tenant, elles sont réparties sur trois secteurs géologiques de l’appellation : sol argilo calcaire, sol argilo-silicieux et sol limono calcaire. Cette diversité de terroir alliée à des vinifications parcellaires permettent d’avoir une palette de typicités lors des assemblages. <br>L’appellation Puisseguin Saint-Émilion est une petite appellation de 750 hectares. Elle fait partie des quatre satellites de Saint-Émilion.", "15.45", "28", "1", "60", "8","8", "16", "chateau_lanbersac_puisseguin_saint_emilion_rouge.png", "1"),
("00198", "St Emilion Grand Cru Château Carteau Rouge", "Un Saint Emilion Grand Cru complexe, épicé et profond. Parfait sur une daube provençale.<br><br>Encépagement : Merlot, Cabernet franc, Cabernet sauvignon<br><br>Dégustation : Robe intense et profonde ; Nez aux notes de fruits noirs et de réglisse ; Bouche ample, épicée, puissante sur des tanins fins et torréfiés. <br>Accord mets/vin : viande rouge, gibier.<br><br>Le vignoble du Château Carteau, 22,30 hectares au sol d’argile et de graves, est situé sur le Coteau sud de Saint Emilion, au pied de la cité. <br>Depuis 1982, application d’un programme de lutte raisonnée avec une sélection rigoureuses et un contrôle de maturité des raisins tout au long de l’année.", "31.95", "28", "1", "72", "8","8", "16", "chateau_carteau_saint_emilion_grand_cru_rouge.png", "1"),
("00199", "St Emilion Grand Cru Grand Barrail-Lamarzelle Figeac", "Un Saint Emilion Grand Cru digne des plus grands. Fin, élégant et charnue, il accompagnera à merveille une épaule d’agneau grillée.<br><br>Encépagement : Merlot, Cabernet franc<br><br>Dégustation : Robe rouge grenat ; Nez puissants et complexe aux notes de fruits noirs mûrs, de violette avec un touche épicée ; Bouche aux notes de fruits noirs et des tannins fins et soyeux. Un vin d'un superbe équilibre et d'un grand potentiel de garde.<br>Accord mets/vin : viande rouge, gibier.<br><br>Le Château Grand Barrail Lamarzelle Figeac réunit toutes les conditions naturelles, humaines et matérielles pour appartenir à l’élite des Grands Crus de Saint-Emilion. Le Château Grand Barrail Lamarzelle Figeac est un vin qui se distingue plus par son élégance et sa finesse que par une structure massive.", "38.2", "9", "1", "72", "8","8", "16", "chateau_grand_barrail_lamarelle_figeac_saint_emilion_grand_cru_rouge.png", "1"),
("00203", "St-Estèphe Héritage de Le Boscq Rouge", "Un Saint Estèphe puissant et généreux. Idéal sur une côtes de boeuf.<br><br>Encépagement : Merlot, Cabernet sauvignon, Petit verdot, Cabernet franc<br><br>Dégustation : Robe intense et profonde ; Nez aux notes de fruits murs ; Bouche souple et ronde aux notes de fruits noirs avec une finale boisée et vanillée.<br><br>Accord mets/vin : viande rouge, gibier.<br><br>Le Château Le Boscq fait partie des grands classiques de l’appellation Saint-Estèphe. Il est le second vin du Château Le Boscq.", "24.1", "9", "1", "73", "8","8", "16", "chateau_le_boscq_heritage_saint_estephe_rouge.png", "1"),
("00228", "BIB Côteaux Varois Rosé 5 L Domaine La Goujonne ", "Un vin rosé sec et élégant.<br><br>Encépagement : assemblage de différents cépages phares de la Provence.<br><br>Dégustation : sec, fruité avec une belle tension.<br>Accord mets/vin : apéritif, grillades, poissons à chair fine.<br><br>Le Domaine de la Goujonne situé à Tourves, est un domaine familiale créée en 1967. Sa situation géographique permet de proposer différentes appellations de Provence : IGP Var, Coteaux-Varois-En-Provence, Côtes de Provence.<br>Le domaine travaille en Agriculture Biologique.", "37.15", "14", "4", "23", "32","7", "0", "bag-in-box-domaine-de-la-goujonne-coteaux_varois-rose.png", "1"),
("00229", "BIB Côteaux Varois Rouge 5 L Domaine La Goujonne ", "Un vin rouge fruité et légèrement épicé.<br><br>Encépagement : assemblage de différents cépages phares de la Provence.<br><br>Dégustation : notes de fruits rouges, légèrement épicé et rond.<br>Accord mets/vin : viandes grillées, charcuterie/fromage.<br><br>Le Domaine de la Goujonne situé à Tourves, est un domaine familiale créée en 1967. Sa situation géographique permet de proposer différentes appellations de Provence : IGP Var, Coteaux-Varois-En-Provence, Côtes de Provence.<br>Le domaine travaille en Agriculture Biologique.", "37.15", "14", "4", "23", "32","8", "0", "bag-in-box-domaine-de-la-goujonne-coteaux_varois-rouge.png", "1"),
("00231", "BIB IGP Gard Chardonnay Blanc 5 L Cellier des Chartreux", "Un Vin blanc sec et floral. Idéal pour vos apéritif ou un poisson à chair fine.<br><br>Encépagement : Chardonnay<br><br>Dégustation : vif, frais et floral aux arômes de fruits blancs.<br>Accord mets/vin : viande blanche, poisson.<br><br>Existe en 3L.<br><br>À la croisée des chemins entre Provence et Languedoc, entre Terres du Roy et Cité des Papes, les coopérateurs du Cellier des Chartreux partagent un héritage riche de cultures et d’histoires.<br>En 1612, après 25 ans de travaux, l’Étang de Pujaut, à quelques lieues de la Cité des Papes, est enfin asséché par les Chartreux. C’est en 1929, année de la création du Syndicat Général des Vignerons des Côtes du Rhône, le Cellier des Chartreux voit le jour, motivé par une volonté depuis respectée : créer des vins issus d’un savoir-faire transmis et renouvelé de génération en génération depuis 400 ans.", "24.3", "39", "4", "38", "33","2", "0", "bag_in_box_cellier_des_chartreux_chardonnay_blanc.png", "1"),
("00232", "BIB IGP Gard Chardonnay Blanc 3 L Cellier des Chartreux", "Un vin blanc sec et floral. Idéal pour vos apéritifs ou un poisson à chair fine.<br><br>Encépagement : Chardonnay<br><br>Dégustation : vif, frais et floral aux arômes de fruits blancs.<br>Accord mets/vin : viande blanche, poisson.<br><br>Existe en 5L.<br><br>À la croisée des chemins entre Provence et Languedoc, entre Terres du Roy et Cité des Papes, les coopérateurs du Cellier des Chartreux partagent un héritage riche de cultures et d’histoires.<br>En 1612, après 25 ans de travaux, l’Étang de Pujaut, à quelques lieues de la Cité des Papes, est enfin asséché par les Chartreux. C’est en 1929, année de la création du Syndicat Général des Vignerons des Côtes du Rhône, le Cellier des Chartreux voit le jour, motivé par une volonté depuis respectée : créer des vins issus d’un savoir-faire transmis et renouvelé de génération en génération depuis 400 ans.", "15.6", "39", "4", "38", "33","2", "7", "bag_in_box_cellier_des_chartreux_chardonnay_blanc.png", "1"),
("00233", "BIB IGP Gard Rouge 10 L Cellier des Chartreux", "Un vin rouge léger et fruité parfait un plateau de charcuterie.<br><br>Encépagement : Grenache, Syrah, Carignan, Cinsault<br><br>Dégustation : gourmand, souple et fruité. Arômes de fraise et framboise.<br>Accord mets/vin : viande rouge, charcuterie/fromage.<br><br>Existe en 5L.<br><br>À la croisée des chemins entre Provence et Languedoc, entre Terres du Roy et Cité des Papes, les coopérateurs du Cellier des Chartreux partagent un héritage riche de cultures et d’histoires.<br>En 1612, après 25 ans de travaux, l’Étang de Pujaut, à quelques lieues de la Cité des Papes, est enfin asséché par les Chartreux. C’est en 1929, année de la création du Syndicat Général des Vignerons des Côtes du Rhône, le Cellier des Chartreux voit le jour, motivé par une volonté depuis respectée : créer des vins issus d’un savoir-faire transmis et renouvelé de génération en génération depuis 400 ans.", "31.1", "39", "4", "38", "33","8", "0", "bag_in_box_cellier_des_chartreux_igp_gard_rouge.png", "1"),
("00234", "BIB IGP Gard Rouge 5 L Cellier des Chartreux", "Un vin rouge léger et fruité parfait pour un plateau de charcuterie.<br><br>Encépagement : Grenache, Syrah, Carignan, Cinsault<br><br>Dégustation : gourmand, souple et fruité. Arômes de fraise et framboise.<br>Accord mets/vin : viande rouge, charcuterie/fromage.<br><br>Existe en 10L.<br><br>À la croisée des chemins entre Provence et Languedoc, entre Terres du Roy et Cité des Papes, les coopérateurs du Cellier des Chartreux partagent un héritage riche de cultures et d’histoires.<br>En 1612, après 25 ans de travaux, l’Étang de Pujaut, à quelques lieues de la Cité des Papes, est enfin asséché par les Chartreux. C’est en 1929, année de la création du Syndicat Général des Vignerons des Côtes du Rhône, le Cellier des Chartreux voit le jour, motivé par une volonté depuis respectée : créer des vins issus d’un savoir-faire transmis et renouvelé de génération en génération depuis 400 ans.", "17.3", "39", "4", "38", "33","8", "0", "bag_in_box_cellier_des_chartreux_igp_gard_rouge.png", "1"),
("00235", "BIB IGP Gard Viognier Blanc 5 L Cellier des Chartreux", "Un vin blanc sec et fruité. Idéal pour vos apéritifs ou un porc au caramel.<br><br>Encépagement : Viognier<br><br>Dégustation : sec, vif et frais aux notes de fruits jaunes (abricot, mangue).<br>Accord mets/vin : viande blanche, poisson, plats exotiques.<br><br>À la croisée des chemins entre Provence et Languedoc, entre Terres du Roy et Cité des Papes, les coopérateurs du Cellier des Chartreux partagent un héritage riche de cultures et d’histoires.<br>En 1612, après 25 ans de travaux, l’Étang de Pujaut, à quelques lieues de la Cité des Papes, est enfin asséché par les Chartreux. C’est en 1929, année de la création du Syndicat Général des Vignerons des Côtes du Rhône, le Cellier des Chartreux voit le jour, motivé par une volonté depuis respectée : créer des vins issus d’un savoir-faire transmis et renouvelé de génération en génération depuis 400 ans.", "24.4", "39", "4", "38", "33","2", "0", "bag_in_box_cellier_des_chartreux_viognier_blanc.png", "1"),
("00265", "Aloxe Corton Rouge Jean Luc Maldant", "Un Aloxe Corton structuré et élégant pour accompagner un boeuf sauce piquante.<br><br>Encépagement : Pinot noir<br><br>Dégustation : robe pourpre, nez complexe sur des notes de fruits rouges, de légères épices et de musc, bouche ronde, tanins fins, bel équilibre avec le bois.<br>Accord mets/vin : volailles, joue de boeuf, fromage affiné, cuisine épicé, viande rouge.<br><br>Existe en Magnum.<br><br>Domaine familial depuis des années, le Domaine Maldant-Pauvelot s’étend sur 18 Ha sur les Grands Terroirs de Bourgogne. Jean-Luc Maldant reprends les reines en 2009 et installe le domaine dans la qualité de ses produits.", "43.2", "23", "1", "1", "10","8", "16", "jean_luc_maldant_aloxe_corton_rouge.png", "1"),
("00267", "Bourgogne Côtes Chalonnaises Tête de Cuvée Chardonnay Blanc Buxy", "Un regroupement de vignerons bourguignons passionnés. Les vignes se situent entre Buxy et Saint Gengoux le National et sont généralement en haut de coteaux. Les sols sont peu profonds, parfois à même la roche mère. Les terrains sont très caillouteux. Ses raisins proviennent de sols argilo-calcaires à dominante calcaire de la Côte Chalonnaise. Les pentes sont douces et orientées Est à Sud-Est.<br><br>Encépagement : Chardonnay<br><br>Dégustation : robe or clair ; Nez aromatique aux notes d’agrumes et d’aubépine ; Bouche anisée, beurrée et fruitée.<br>Accord mets/vin : apéritif, poisson, viande blanche.", "12.8", "25", "2", "10", "10","2", "16", "bourgogne_cote_chalonnaise_blanc.png", "1"),
("00268", "Bourgogne Côtes Chalonnaises Tête de Cuvée Pinot Noir Rouge Buxy", "Un regroupement de vignerons bourguignons passionnés. Au nord de la Côte Chalonnaise, le vignoble de Rully s’étend sur des coteaux tourmentés par les plissements successifs et offre des terroirs, des expositions et des altitudes assez variés. L’exposition est pleine Est. Le sous-sol est à dominante argilo-calcaire. Sur ce plateau, le sol est peu profond, la terre est rouge et est composée d’une multitude de petits cailloux qui contraste avec le reflet rouge de la terre.<br><br>Encépagement : Pinot Noir<br><br>Dégustation : robe rouge clair ; Nez léger, fruité sur les fruits rouges et noirs ; Bouche aux notes de griottes, puissante avec des tanins fondus.<br>Accord mets/vin : gibier, fromages.", "13.8", "25", "1", "10", "10","8", "16", "bourgogne_cote_chalonnaise_rouge.png", "1"),
("00269", "Bourgogne Chardonnay Blanc Jean-Luc Maldant", "Un Bourgogne Chardonnay ample, beurré et brioché qui accompagnera à merveille un ristto aux champignons.<br><br>Encépagement : Chardonnay<br><br>Dégustation : robe jaune pâle, nez brioché, beurré et frais, bouche ronde et minérale, bel équilibre et notes de fruits blancs.<br>Accord mets/vin : fruits de mer, poisson.<br><br>Existe en Magnum.<br><br>Domaine familial depuis des années, le Domaine Maldant-Pauvelot s’étend sur 18 Ha sur les Grands Terroirs de Bourgogne. Jean-Luc Maldant reprends les reines en 2009 et installe le domaine dans la qualité de ses produits.", "19.5", "23", "2", "9", "10","2", "16", "jean_luc_maldant_chardonnay_blanc.png", "1"),
("00271", "Bourgogne Pinot Noir Rouge Jean-Luc Maldant", "Un Bourgogne Pinot Noir léger et fruité qui s'accordera parfaitement avec une côte de boeuf.<br><br>Encépagement : Pinot Noir<br><br>Dégustation : robe rubis, nez fruité de fruits rouges, fraise, framboise, bouche fruitée et légère<br>Accord mets/vin : grillades, pique-nique.<br><br>Existe en Magnum.<br><br>Domaine familial depuis des années, le Domaine Maldant-Pauvelot s’étend sur 18 Ha sur les Grands Terroirs de Bourgogne. Jean-Luc Maldant reprends les reines en 2009 et installe le domaine dans la qualité de ses produits.", "19.5", "23", "1", "11", "10","8", "16", "jean_luc_maldant_pinot_noir_rouge.png", "1"),
("00272", "Bourgogne Pinot Noir Rouge Magnum  Jean-Luc Maldant", "Un Bourgogne Pinot Noir léger et fruité qui s'accordera parfaitement avec une côte de boeuf.<br><br>Encépagement : Pinot Noir<br><br>Dégustation : robe rubis, nez fruité de fruits rouges, fraise, framboise, bouche fruitée et légère<br>Accord mets/vin : grillades, pique-nique.<br><br>Existe en 75cl.<br><br>Domaine familial depuis des années, le Domaine Maldant-Pauvelot s’étend sur 18 Ha sur les Grands Terroirs de Bourgogne. Jean-Luc Maldant reprends les reines en 2009 et installe le domaine dans la qualité de ses produits.", "43.2", "23", "1", "11", "10","8", "19", "jean_luc_maldant_pinot_noir_rouge.png", "1"),
("00275", "Châblis 1er Cru Fourchaume Blanc Alain Geoffroy", "Un Chablis 1er Cru complexe et élégant qui pourra accompagner un risotto aux gambas.<br><br>Encépagement : Chardonnay<br><br>Dégustation : Robe or blanc ; Nez brioché et intense de fleurs blanches distinguées ; Bouche puissante, riche et élégante. Finale longue et pleine de finesse<br>Accord mets/vin : crustacés, poissons fins, fromage affiné.<br><br>Le Domaine Alain Geoffroy installé à Beine, près de Chablis, réalise la production, la récolte et la vente de vins blancs issus de cépages Chardonnay.<br>Honoré Geoffroy, vers 1850, étendit l’exploitation sur les communes de Beine et la Chapelle Vaupelteigne, au coeur du vignoble chablisien.<br>Alain Geoffroy fit passer la propriété au niveau de Domaine. Digne héritier de toutes les générations, il a su lui conserver le caractère familial et traditionnel.<br>L’objectif est produire des vins de Chablis tout en finesse et en élégance dans le respect du terroir chablisien.", "36.65", "1", "2", "15", "10","2", "16", "alain_geoffroy_chablis_premier_cru_fourchaume_blanc.png", "1"),
("00276", "Châblis 1er Cru Vau Ligneau Blanc Alain Geoffroy", "Un Chablis 1er Cru minéral et aromatique. Idéal sur un plateau de fruits de mer.<br><br>Encépagement : Chardonnay<br><br>Dégustation : Robe or blanc ; Nez dense, floral aux notes d’agrumes et de fleurs blanches ; Bouche ample, ronde, minérale sur les fruits frais et une longue finale.<br>Accord mets/vin : crustacés, poissons fins, fromage affiné.<br><br>Le Domaine Alain Geoffroy installé à Beine, près de Chablis, réalise la production, la récolte et la vente de vins blancs issus de cépages Chardonnay.<br>Honoré Geoffroy, vers 1850, étendit l’exploitation sur les communes de Beine et la Chapelle Vaupelteigne, au coeur du vignoble chablisien.<br>Alain Geoffroy fit passer la propriété au niveau de Domaine. Digne héritier de toutes les générations, il a su lui conserver le caractère familial et traditionnel.<br>L’objectif est produire des vins de Chablis tout en finesse et en élégance dans le respect du terroir chablisien.", "30.15", "1", "2", "15", "10","2", "16", "alain_geoffroy_chablis_premier_cru_vau_ligneau_blanc.png", "1"),
("00289", "Ladoix Rouge Jean-Luc Maldant", "Un Ladoix complexe et structuré. Idéal sur un suprûme de volaille légèrement relevé.<br><br>Encépagement : Pinot Noir<br><br>Dégustation : Robe rubis profond ; Nez aux notes de fruits noirs, d’épices et de poivre noir ; Bouche ample, fraîche avec des tanins puissants et sanguins.<br>Accord mets/vin : grillades, gibier.<br><br>Domaine familial depuis des années, le Domaine Maldant-Pauvelot s’étend sur 18 Ha sur les Grands Terroirs de Bourgogne. Jean-Luc Maldant reprends les reines en 2009 et installe le domaine dans la qualité de ses produits.", "33.5", "23", "1", "44", "10","8", "16", "jean_luc_maldant_ladoix_rouge.png", "1"),
("00305", "Santenay La Cassière Rouge Jean-Luc Maldant", "Un Santenay plein de finesse et de fruits. Parfait sur des grillades.<br><br>Encépagement : Pinot Noir<br><br>Dégustation : Robe rouge sombre ; Nez fruité de fruits rouges, de violette et de réglisse ; Bouche ample, charpentée avec des tanins fermes et discrets.<br>Accord mets/vin : grillades, gibier.<br><br>Existe en Magnum.<br><br>Domaine familial depuis des années, le Domaine Maldant-Pauvelot s’étend sur 18 Ha sur les Grands Terroirs de Bourgogne. Jean-Luc Maldant reprends les reines en 2009 et installe le domaine dans la qualité de ses produits.", "27", "23", "1", "67", "10","8", "16", "jean_luc_maldant_santenay_rouge.png", "1"),
("00306", "Savigny-Les-Beaune Rouge Jean-Luc Maldant", "Un Savigny-les-beaune rouge plein de finesse et de fruit pouvant s'accorder avec un faux filet frites.<br><br>Encépagement : Pinot Noir<br><br>Dégustation : Robe foncé profond ; Nez aux notes de fruits noirs, d’épices et de poivre noir ; Bouche ample, poivrée avec es tanins souples et une finale pleine de finesse et de légèretée.<br>Accord mets/vin : lapin rôti en sauce, queue de lotte au vin rouge.<br><br>Existe en Magnum.<br><br>Domaine familial depuis des années, le Domaine Maldant-Pauvelot s’étend sur 18 Ha sur les Grands Terroirs de Bourgogne. Jean-Luc Maldant reprends les reines en 2009 et installe le domaine dans la qualité de ses produits.", "28.1", "23", "1", "70", "10","8", "16", "jean_luc_maldant_savigny_les_beaune_rouge.png", "1"),
("00328", "Champagne Haton Cuvée Classic", "Un Champagne brut élégant, fruité et passe partout. Idéal pour les évènements familliaux <br><br>Encépagement : Pinot noir, Pinot meunier<br><br>Dégustation : Bulles fines, nez aux notes d’agrumes, et de fleurs séchées ; bouche fraîche aux notes d’agrumes et belle finale.<br><br>La maison de Champagne Jean-Noël Haton a été fondée en 1928. Entreprise familiale située à Damery, elle possède son propre vignoble dans les meilleurs crus de Champagne. Sous l’égide de René Haton, héritier du fondateur Octave Haton, la Maison connaît un véritable essor dans les années 70. <br><br>Aujourd’hui, Jean-Noël Haton et son fils Sébastien continuent à élaborer des Champagnes selon des pratiques traditionnelles et méticuleuses : respect des sols, maîtrise des rendements, quête de la maturité optimale, vinification parcellaire… avec pour seul objectif la recherche de l’excellence. Ils poursuivent le travail engagé par le fondateur Octave Haton selon son adage : « La perfection exige la maîtrise de chaque détail. C’est l’expression de l’ensemble qui fait un Champagne d’exception. »<br><br>Le Champagne est conservé jusqu’à 3 ans pour les cuvées non millésimées, 6 à 8 ans pour les cuvées Extra.", "23.9", "6", "5", "16", "12","0", "16", "champagne_jean_noel_haton_classic.png", "1"),
("00329", "Champagne Soutiran Grand Cru Blanc de Blancs", "Un Champagne Grand Cru Blanc de Blanc soyeux, élégant aux bulles fines pouvant s'accorder sur en apéritif ou sur un dessert au chocolat.<br><br>Encépagement : Chardonnay<br><br>Dégustation : Bulles Fines  Nez d’agrumes et floral ; Bouche élégante, minérale et aux notes de fruits blancs.<br><br>La Maison Soutiran est un domaine familliale et a été créée par Gérard Soutiran dans les années 1950. C'est Valérie, la petite fille de Gérard, qui développa le côté commercial, le nom est alors devenu 'Champagne Soutiran'. Patrick, le mari de Valérie pris le relai pour l'élaboration et la production du vin.<br>Le domaine compte plus de 6 Ha de vignes, sur le terroir d'Ambonnay. Le Pinot Noir et le Chardonnay sont plantés sur des collines exposées sud et sud-est, garantissant une exposition très ensoleillée.<br>La Maison Soutiran étant classée en 'Grand Cru', exprime toute la rareté de ce Champagne. En effet, seul 17 Maisons sont classées 'Grand Cru', sur un total de 320 Maisons environ.<br>La Maison Soutiran s'atèle a produire des champagnes riches, racés, expressifs avec une texture crémeuse soulignée par une salinité minérale issur des sols calcaires et crayeux.", "50.6", "8", "5", "16", "12","0", "16", "champagne_soutiran_blanc_de_blanc.png", "1"),
("00330", "Champagne Soutiran Grand Cru Collection Privée ", "Un Champagne Grand Cru d'une finesse inégalable qui pourra accompagner un poisson ou un plateau de fruits de mer.<br><br>Encépagement : Chardonnay, Pinot noir<br><br>Dégustation : Bulles fines ; Nez d’agrumes, de fruits secs et torréfaction ; Bouche ample et onctueuse avec une belle finale.<br><br>Existe en Magnum.<br><br>La Maison Soutiran est un domaine familliale et a été créée par Gérard Soutiran dans les années 1950. C'est Valérie, la petite fille de Gérard, qui développa le côté commercial, le nom est alors devenu 'Champagne Soutiran'. Patrick, le mari de Valérie pris le relai pour l'élaboration et la production du vin.<br>Le domaine compte plus de 6 Ha de vignes, sur le terroir d'Ambonnay. Le Pinot Noir et le Chardonnay sont plantés sur des collines exposées sud et sud-est, garantissant une exposition très ensoleillée.<br>La Maison Soutiran étant classée en 'Grand Cru', exprime toute la rareté de ce Champagne. En effet, seul 17 Maisons sont classées 'Grand Cru', sur un total de 320 Maisons environ.<br>La Maison Soutiran s'atèle a produire des champagnes riches, racés, expressifs avec une texture crémeuse soulignée par une salinité minérale issur des sols calcaires et crayeux.", "52.3", "8", "5", "16", "12","0", "16", "champagne_soutiran_collection_privee.png", "1"),
("00332", "Champagne Soutiran Grand Cru Signature Brut", "Un Champagne Grand Cru aux bulles fines et aux notes légèrement brioché. Idéal en apéritif ou sur un dessert au chocolat.<br><br>Encépagement : Pinot noir, Chardonnay<br><br>Dégustation : Bulles fines ; Nez de poire compotée, pêche et légèrement torréfié ; Bouche de fruits exotiques, minérale et saline.<br><br>Existe en Magnum, Jéroboam et en 37,5cl.<br><br>La Maison Soutiran est un domaine familliale et a été créée par Gérard Soutiran dans les années 1950. C'est Valérie, la petite fille de Gérard, qui développa le côté commercial, le nom est alors devenu 'Champagne Soutiran'. Patrick, le mari de Valérie pris le relai pour l'élaboration et la production du vin.<br>Le domaine compte plus de 6 Ha de vignes, sur le terroir d'Ambonnay. Le Pinot Noir et le Chardonnay sont plantés sur des collines exposées sud et sud-est, garantissant une exposition très ensoleillée.<br>La Maison Soutiran étant classée en 'Grand Cru', exprime toute la rareté de ce Champagne. En effet, seul 17 Maisons sont classées 'Grand Cru', sur un total de 320 Maisons environ.<br>La Maison Soutiran s'atèle a produire des champagnes riches, racés, expressifs avec une texture crémeuse soulignée par une salinité minérale issur des sols calcaires et crayeux.", "43.9", "8", "5", "16", "12","0", "16", "champagne_soutiran_signature.png", "1"),
("00333", "Champagne Thiénot Brut Blanc", "Un Champagne frais et fruité, idéal en apéritif ou sur un dessert aux fruits.<br><br>Encépagement : Chardonnay, Pinot noir, Pinot meunier<br><br>Dégustation : Bulles fines, nez aux notes printanières, floral et fruits blanc ; bouche fine, fraîche et élégante.<br><br>Maison créée en 1985 par leur père Alain dont ils sont désormais les gardiens, guide Garance et Stanislas Thiénot. Alain Thiénot a constitué un vignoble conséquent de 30 hectares de grands crus dans la grande vallée historique à Aÿ et dans la Côte des Blancs et de premiers crus à Dizy et Cumières et sur les Coteaux au Sud d’Epernay.", "37.9", "9", "5", "16", "12","0", "16", "champagne_thienot_brut.png", "1"),
("00334", "Champagne Thiénot Brut Rosé", "Un Champagne rosé fruité et élégant pour accompagner une tarte aux fraises.<br><br>Encépagement : Pinot noir, Pinot meunier<br><br>Dégustation : Bulles fines, nez aux notes de fruits rouges ; bouche élégante et fruitée avec une finale pleine de finesse.<br><br>Maison créée en 1985 par leur père Alain dont ils sont désormais les gardiens, guide Garance et Stanislas Thiénot. Alain Thiénot a constitué un vignoble conséquent de 30 hectares de grands crus dans la grande vallée historique à Aÿ et dans la Côte des Blancs et de premiers crus à Dizy et Cumières et sur les Coteaux au Sud d’Epernay.", "49.9", "9", "5", "16", "12","7", "16", "champagne_thienot_rose.png", "1"),
("00340", "Cremant Bg. B.Lapierre Chardonnay Brut", "Un Crémant de Bourgogne fin et aérien. Idéal sur un apértif entre amis.<br><br>Encépagement : Chardonnay, Aligoté<br><br>Dégustation : Bulles élégantes et fines ; Nez pur et aériens aux notes de fruits blancs ; Bouche souple, complexe aux arômes de brioche et d’amande grillée.<br>Accord mets/vin : apéritif, dessert, poisson.<br><br>Les multiples failles qui découpent la région ont donné naissance à autant de terrains différents par leur nature, leur orientation et leur âge. Les vignes se situent en Cote Chalonnaise et dans le nord du Mâconnais. Ses raisins proviennent des sols argilo-calcaires à argilo siliceux sur l’aire d’appellation Bourgogne.", "12.5", "25", "6", "31", "10","2", "16", "cremant_de_bourgogne_blanc_de_blanc.png", "1"),
("00346", "Derezla - Tokaji Vt Furmint Blanc 50 Cl", "Un Tokaji moelleux, idéal sur un foie gras.<br><br>Encépagement : Furmint (Vendanges Tardives)<br><br>Dégustation : Robe jaune or tuilé ; Nez intense et complexe aux notes de fruits confits ; Bouche fraîche et sucrée, bien équilibrée.<br>Accord mets/vin : apéritif, foie gras, dessert au fruits frais.<br><br>La cave Bourvence vous propose les vins de Vinho Sélection qui est importateur de vins du monde. <br>Fruit d'un travail de référencement passionnant, qui se déroule parfois sur plusieurs saisons, la sélection vous permettra de découvrir des vins étonnants, issus de cépages autochtones peu connus, comme le Zinfandel aux USA, le Torrontes en Argentine, le Furmint en Hongrie, le Carménère au Chili et bien d'autres encore. Une invitation à partager de nouvelles notes gustatives.", "21.4", "41", "2", "0", "22","2", "13", "hongrie_chateau_dereszla_tokaji_furmint_blanc.png", "1"),
("00353", "Bandol La Chretienne La Lecque Rouge", "Un vin animal et fluide pour amateurs de petits gibiers.<br><br>Encépagement : Mourvèdre, Carignan<br><br>Dégustation : robe rouge sombre, nez de fruits noirs, bouche aux tanins enrobés et aux notes de fruits noirs bien mûrs avec une finale parfumée et poivrée.<br>Accord mets/vin : viande grillée, gibier en sauce.<br><br>Existe en Magnum.<br><br>Situé à St Cyr/Mer, les vins de ce domaine seront ravir tous les palets !!<br>19 hectares de vignes sont répartis sur les communes de Saint Cyr, La Cadière et le Castellet. La philosophie du domaine est de pratiquer l’agriculture régénérative dont l’objectif est d’augmenter la biodiversité.", "29.6", "16", "1", "2", "32","8", "16", "domaine_chretienne_lecque_rouge.png", "1"),
("00362", "Beaumes De Venise Terrissimo Rouge", "Un Beame de Venise puissant et gourmand. Idéal sur une côtes de boeuf grillée.<br><br>Encépagement : Grenache, Syrah<br><br>Dégustation : Robe rouge éclatante ; Nez de fruits mûrs et épicé ; Bouche puissante et complexe aux notes de fruits rouges compotés.<br>Accord mets/vin : viande grillée, gibier.<br><br>Existe en Magnum.<br><br>La société Rhône Rive Gauche nous propose les vins de la Cave de Gigondas qui est située au coeur des Dentelles de Montmirail, sur la commune de Gigondas. Le vignoble s’étend sur 260 hectares produisant les crus Gigondas, Vacqueyras, Beaumes de Venise. <br><br>Créée en 1956 par une poignée de vignerons, dont la volonté était la commercialisation de leur production en bouteilles, et qui compte aujourd’hui 83 adhérents et représente 15% de l’appellation, 128 ha en AOP Gigondas, (260 ha toutes AOP confondues). ", "13.5", "35", "1", "4", "33","8", "16", "rhone_rive_gauche_beaume_de_venise_terrissimo_rouge.png", "1"),
("00385", "Cdp Gamali La Goujonne Blanc", "Un Côtes de Provence blanc sec, ample d'une belle complexité aromatique. Idéal sur une dorade en papillote.<br><br>Encépagement : assemblage de différents cépages phares de la Provence.<br><br>Dégustation : robe jaune pâle brillante, nez complexe, fruité et floral, bouche sur des notes de fruits mûrs, agréable et aérienne.<br>Accord mets/vin : poissons grillés, volaille crémée.<br><br>Le Domaine de la Goujonne situé à Tourves, est un domaine familiale créée en 1967. Sa situation géographique permet de proposer différentes appellations de Provence : IGP Var, Coteaux-Varois-En-Provence, Côtes de Provence.<br>Le domaine travaille en Agriculture Biologique.", "12.3", "14", "2", "25", "32","2", "16", "domaine_goujonne_gamali_blanc.png", "1"),
("00386", "Cdp Gamali La Goujonne Rose", "Un Côtes de Provence rosé sec, rond et fruité parfait sur des grillades ou une paella.<br><br>Encépagement : assemblage de différents cépages phares de la Provence.<br><br>Dégustation : robe rose pâle, nez de fleurs blanches et de fruits secs, la bouche est équilibrée et ronde, finale sur des notes d’agrumes.<br>Accord mets/vin : volaille, apéritif, grillades, salade d’été.<br><br>Le Domaine de la Goujonne situé à Tourves, est un domaine familiale créée en 1967. Sa situation géographique permet de proposer différentes appellations de Provence : IGP Var, Coteaux-Varois-En-Provence, Côtes de Provence.<br>Le domaine travaille en Agriculture Biologique.", "12.3", "14", "3", "25", "32","7", "16", "domaine_goujonne_gamali_rose.png", "1"),
("00387", "Cdp Gamali La Goujonne Rouge", "Un Côtes de Provence rouge épicé et fruité qui pourra accompagner une côte de boeuf.<br><br>Encépagement : assemblage de différents cépages phares de la Provence.<br><br>Dégustation : robe rouge profond, nez de fruits mûrs, de cerises, bouche ronde aux tanins mûrs, finale longue sur des notes de fruits rouges et noirs.<br>Accord mets/vin : Grillades, viandes rouges, charcuterie/fromage.<br><br>Le Domaine de la Goujonne situé à Tourves, est un domaine familiale créée en 1967. Sa situation géographique permet de proposer différentes appellations de Provence : IGP Var, Coteaux-Varois-En-Provence, Côtes de Provence.<br>Le domaine travaille en Agriculture Biologique.", "12.3", "14", "1", "25", "32","8", "16", "domaine_goujonne_gamali_rouge.png", "1"),
("00406", "Rasteau La Gardine Rouge", "Un Rasteau puissant et généreux qui accompagnera à merveille un côtes de boeuf grillée.<br><br>Encépagement : Grenache, Syrah<br><br>Dégustation : Robe grenat aux reflets violets ; Nez fruité au notes de fruits noirs et d'épices douces ; Bouche élégante, généreuse et fruitée aux notes de mûres, de cassis et de cerises à clafoutis.<br>Accord mets/vin : viande grillée, une épaule d'agneau au thym.<br><br>Existe en Magnum et en Jeroboam.<br><br>La tradition viticole de la famille Brunel remonte au 17ème siècle. Gaston Brunel, un célèbre négociant, acheta le Château de la Gardine à Châteauneuf du Pape en 1945. Le domaine est maintenant tenu par ses deux fils, avec l’aide de leurs épouses et de leurs enfants. Le domaine s’étend sur 52 ha de vignes (48 ha de rouge et 4 ha de blanc).<br>La bouteille Gardine, originale et élégante, est née d'un heureuxhasard...<br>Lorsqu’il voulut agrandir sa cave pour la première fois, en creusant dans le sol, Gaston Brunel trouva une très vieille bouteille qui avait été soufflée à la bouche. La forme lui plut beaucoup, il décida donc d’embouteiller tout son vin dans une bouteille similaire. Au début il ne parvint pas à trouver un fabricant en France capable de reproduire cette bouteille, tant elle était particulière. Il les fit donc fabriquer en Italie. Le premier millésime dans la bouteille Gardine fut 1964. La bouteille est désormais fabriquée en France dans un moule créé spécifiquement pour la famille Brunel.", "20.55", "36", "1", "61", "33","8", "16", "gardine_rasteau_rouge.png", "1"),
("00407", "Cdr Regulus Clavel Blanc", "Un Côtes du Rhône blanc sec et aromatique, parfait en apéritif ou sur un porc au caramel.<br><br>Encépagement : Viognier et Grenache.<br><br>Dégustation : Robe jaune pâle, Nez au notes de fruits d'agrumes et de fruits jaunes, Bouche ronde et fraîche aux notes de pêche blanche et de citron.<br>Accord mets/vin : apéritif, plat exotique.<br><br>Le domaine est situé à Saint-Gervais, petit village aux accents typiques du midi qui fait partie des dix-huit dénominations Côtes du Rhône Villages des vignobles de la Vallée du Rhône. Porte d’entrée de la vallée de la Cèze, bercé de mistral, son climat méditerranéen subit l’influence des Cévennes qui apportent de la fraicheur à ses vins.<br><br>Descendante d’une famille qui travaille la vigne depuis 1640, Claire Clavel, Femme Vigneronne, est épaulée par son père Denis. Elle conduit les 80 hectares de son domaine.", "9.5", "13", "2", "26", "33","2", "16", "domaine_clavel_regulus_blanc.png", "1"),
("00408", "Cdr Regulus Clavel Rose", "Un Côtes du Rhône rosé sec et fruité. Parfait pour vos apéritifs entre amis.<br><br>Encépagement : Grenache, Cinsault, Syrah<br><br>Dégustation : Robe rose aux reflets saumonnés, Nez au notes de fruits de fruits rouges, Bouche ronde et fraîche aux notes de framboise et de fraises des bois.<br>Accord mets/vin : apéritif, grillades, salade d’été.<br><br>Le domaine est situé à Saint-Gervais, petit village aux accents typiques du midi qui fait partie des dix-huit dénominations Côtes du Rhône Villages des vignobles de la Vallée du Rhône. Porte d’entrée de la vallée de la Cèze, bercé de mistral, son climat méditerranéen subit l’influence des Cévennes qui apportent de la fraicheur à ses vins.<br><br>Descendante d’une famille qui travaille la vigne depuis 1640, Claire Clavel, Femme Vigneronne, est épaulée par son père Denis. Elle conduit les 80 hectares de son domaine.", "9.5", "13", "3", "26", "33","7", "16", "domaine_clavel_regulus_rose.png", "1"),
("00409", "Cdr Regulus Clavel Rouge", "Un Côtes du Rhône rouge léger et fruité. Idéal sur un plateau de charcuterie/fromages.<br><br>Encépagement : Grenache, Syrah et Marselan.<br><br>Dégustation : Robe rouge carmin, Nez puissant et charmeur aux notes de fruits rouges et de cacao, Bouche souple et ronde avec des tannins soyeux.<br>Accord mets/vin : apéritif, viande grillée.<br><br>Existe en Magnum.<br><br>Le domaine est situé à Saint-Gervais, petit village aux accents typiques du midi qui fait partie des dix-huit dénominations Côtes du Rhône Villages des vignobles de la Vallée du Rhône. Porte d’entrée de la vallée de la Cèze, bercé de mistral, son climat méditerranéen subit l’influence des Cévennes qui apportent de la fraicheur à ses vins.<br><br>Descendante d’une famille qui travaille la vigne depuis 1640, Claire Clavel, Femme Vigneronne, est épaulée par son père Denis. Elle conduit les 80 hectares de son domaine.", "9.5", "13", "1", "26", "33","8", "16", "domaine_clavel_regulus_rouge.png", "1"),
("00411", "Cdr Village Chusclan Cordelia Rouge", "Un Côtes du Rhône Villages rouge charnue et épicé. Idéal sur une côtes de boeuf.<br><br>Encépagement : Grenache, Syrah.<br><br>Dégustation : Robe rouge carmin, Nez puissant et complexe aux notes de garrigue et de fruit mûrs, Bouche souple et ronde avec des tannins soyeux.<br>Accord mets/vin : viande grillée, fromage de brebis.<br><br>Existe en Magnum.<br><br>Le domaine est situé à Saint-Gervais, petit village aux accents typiques du midi qui fait partie des dix-huit dénominations Côtes du Rhône Villages des vignobles de la Vallée du Rhône. Porte d’entrée de la vallée de la Cèze, bercé de mistral, son climat méditerranéen subit l’influence des Cévennes qui apportent de la fraicheur à ses vins.<br><br>Descendante d’une famille qui travaille la vigne depuis 1640, Claire Clavel, Femme Vigneronne, est épaulée par son père Denis. Elle conduit les 80 hectares de son domaine.", "11.9", "13", "1", "28", "33","8", "16", "domaine_clavel_cordelia_rouge.png", "1"),
("00414", "Cdr Village St Gervais Clair De Lune Blc", "Un Côtes du Rhône Villages blanc sec, ample et aromatique. Idéal sur un foie gras ou sur un poulet curry.<br><br>Encépagement : Viognier, Roussanne.<br><br>Dégustation : Robe jaune or aux reflets verveinne, Nez complexe aux notes de fleurs blanches et de fruits compotés, Bouche gourmande, ample aux notes de pêche et de poire avec une pointe minérale.<br>Accord mets/vin : plat exotique, foie gras.<br><br>Le domaine est situé à Saint-Gervais, petit village aux accents typiques du midi qui fait partie des dix-huit dénominations Côtes du Rhône Villages des vignobles de la Vallée du Rhône. Porte d’entrée de la vallée de la Cèze, bercé de mistral, son climat méditerranéen subit l’influence des Cévennes qui apportent de la fraicheur à ses vins.<br><br>Descendante d’une famille qui travaille la vigne depuis 1640, Claire Clavel, Femme Vigneronne, est épaulée par son père Denis. Elle conduit les 80 hectares de son domaine.", "18.5", "13", "2", "29", "33","2", "16", "domaine_clavel_clair_de_lune_blanc.png", "1"),
("00415", "Cdr Village St Gervais Clair De Lune Rge", "Un Côtes du Rhône rouge élégant et charnue. Idéal sur un faux filet sauce aux cèpes.<br><br>Encépagement : Grenache, Syrah et Mourvèdre<br><br>Dégustation : Robe rouge grenat, Nez puissant et intense aux notes de fruits noirs, d'épices et de cuir, Bouche ample et ronde, bien équilibré sur une finale aux notes réglisées.<br>Accord mets/vin : viande épicée, en sauce.<br><br>Existe en Magnum et Jeroboam.<br><br>Le domaine est situé à Saint-Gervais, petit village aux accents typiques du midi qui fait partie des dix-huit dénominations Côtes du Rhône Villages des vignobles de la Vallée du Rhône. Porte d’entrée de la vallée de la Cèze, bercé de mistral, son climat méditerranéen subit l’influence des Cévennes qui apportent de la fraicheur à ses vins.<br><br>Descendante d’une famille qui travaille la vigne depuis 1640, Claire Clavel, Femme Vigneronne, est épaulée par son père Denis. Elle conduit les 80 hectares de son domaine.", "16.5", "13", "1", "29", "33","8", "16", "domaine_clavel_clair_de_lune_rouge.png", "1"),
("00416", "Cdr Village St Gervais Syrius Rouge", "Un Côtes du Rhône rouge épicé et fruité. Idéal sur des cotelletes d'agneau grillées.<br><br>Encépagement : Grenache, Syrah.<br><br>Dégustation : Robe rouge carmin ; Nez complexe aux notes de fruits rouges confiturés et d’épices ; Bouche riche, ample avec des tanins soyeux.<br>Accord mets/vin : gibier, viande rouge grillée.<br><br>Existe en Magnum et Jeroboam.<br><br>Le domaine est situé à Saint-Gervais, petit village aux accents typiques du midi qui fait partie des dix-huit dénominations Côtes du Rhône Villages des vignobles de la Vallée du Rhône. Porte d’entrée de la vallée de la Cèze, bercé de mistral, son climat méditerranéen subit l’influence des Cévennes qui apportent de la fraicheur à ses vins.<br>Descendante d’une famille qui travaille la vigne depuis 1640, Claire Clavel, Femme Vigneronne, est épaulée par son père Denis. Elle conduit les 80 hectares de son domaine.", "11.9", "13", "1", "29", "33","8", "16", "domaine_clavel_syrius_rouge.png", "1"),
("00418", "Ch9 Du Pape La Gardine Blanc", "Un Châteauneauf-du-Pape blanc d'une grande finesse qui pourra accompagner un risotto aux coquillages.<br><br>Encépagement : Roussanne, Grenache blanc, Clairette, Bourboulenc<br><br>Dégustation : Nez aux notes florales fines et vives ; Bouche rondeet aromatique sur une longue finale.<br>Accord mets/vin : poissons à chair raffinée, viandes blanches, fromage de chèvre affiné.<br><br>La tradition viticole de la famille Brunel remonte au 17ème siècle. Gaston Brunel, un célèbre négociant, acheta le Château de la Gardine à Châteauneuf du Pape en 1945. Le domaine est maintenant tenu par ses deux fils, avec l’aide de leurs épouses et de leurs enfants. Le domaine s’étend sur 52 ha de vignes (48 ha de rouge et 4 ha de blanc).<br>La bouteille Gardine, originale et élégante, est née d'un heureuxhasard...<br>Lorsqu’il voulut agrandir sa cave pour la première fois, en creusant dans le sol, Gaston Brunel trouva une très vieille bouteille qui avait été soufflée à la bouche. La forme lui plut beaucoup, il décida donc d’embouteiller tout son vin dans une bouteille similaire. Au début il ne parvint pas à trouver un fabricant en France capable de reproduire cette bouteille, tant elle était particulière. Il les fit donc fabriquer en Italie. Le premier millésime dans la bouteille Gardine fut 1964. La bouteille est désormais fabriquée en France dans un moule créé spécifiquement pour la famille Brunel.", "50.55", "36", "2", "17", "33","2", "16", "gardine_chateau_neuf_du_pape_tradition_blanc.png", "1"),
("00419", "Condrieu Le Mornieux Lionel Faury Blanc", "Un Condrieu ample et d'une gfrande finesse. Idéal sur une queue de lotte au four.<br><br>Encépagement : Viognier<br><br>Dégustation : Robe brillante jaune pâle ; Nez complexe, floral (violette, fleur d'acacia) et aux notes de fruits jaunes (abricot, pêche) ; Bouche riche, ronde et fraîche.<br>Accord mets/vin : volaille crémée, poissons à chair grasse.<br><br>Philippe Faury a créé ce domaine en 1979 à Chavanay, dans le hameau de la Ribaudy, Berceau de la famille. Son plus jeune fils, Lionel, s'est installé en 2005, après une expérience en Australie.<br><br>Domaine de 17Ha, Lionel Faury s'atèle à produire des vins qui expriment la diversité des terroirs en appellations Condrieu, Saint Joseph, Côte-Rôtie, et en IGP Rhodanienne.", "37.7", "30", "2", "20", "33","2", "16", "domaine_lionel_faury_condrieu_mornieux_blanc.png", "1"),
("00445", "Cot.Varois La Goujonne Reine Marie Rouge", "Un Coteaux Varois rouge épicé et gourmand, idéal pour sur un faux filet frites.<br><br>Encépagement : assemblage de différents cépages phares de la Provence.<br><br>Dégustation : Robe rouge et violacée, vin légèrement épicé et rond.<br>Accord mets/vin : viandes grillées, charcuterie/fromage.<br><br>Le Domaine de la Goujonne situé à Tourves, est un domaine familiale créée en 1967. Sa situation géographique permet de proposer différentes appellations de Provence : IGP Var, Coteaux-Varois-En-Provence, Côtes de Provence.<br>Le domaine travaille en Agriculture Biologique.", "9.75", "14", "1", "23", "32","8", "16", "domaine_goujonne_reine_marie_rouge.png", "1"),
("00446", "Cot.Varois La Goujonne Reine Marie Blanc", "Un Coteaux Varois blanc sec, léger et aérien, idéal sur une dorade.<br><br>Encépagement : assemblage de différents cépages phares de la Provence.<br><br>Dégustation : robe jaune clair, nez et bouche d'agrumes et fruits exotiques, finale fraiche et longue.<br>Accord mets/vin : volailles, poissons grillés, fromage persillé.<br><br>Le Domaine de la Goujonne situé à Tourves, est un domaine familiale créée en 1967. Sa situation géographique permet de proposer différentes appellations de Provence : IGP Var, Coteaux-Varois-En-Provence, Côtes de Provence.<br>Le domaine travaille en Agriculture Biologique.", "9.75", "14", "2", "23", "32","2", "16", "domaine_goujonne_reine_marie_blanc.png", "1"),
("00447", "Cot.Varois La Goujonne Reine Marie Rose", "Un Coteaux Varois rosé sec et léger parfait pour des grillades.<br><br>Encépagement : assemblage de différents cépages phares de la Provence.<br><br>Dégustation : robe rosée, nez fruité sur les fruits rouges, bouche fraiche et longue.<br>Accord mets/vin : apéritif, grillades, salade d’été.<br><br>Le Domaine de la Goujonne situé à Tourves, est un domaine familiale créée en 1967. Sa situation géographique permet de proposer différentes appellations de Provence : IGP Var, Coteaux-Varois-En-Provence, Côtes de Provence.<br>Le domaine travaille en Agriculture Biologique.", "9.75", "14", "3", "23", "32","7", "16", "domaine_goujonne_reine_marie_rose.png", "1"),
("00453", "Igp Gard Origine Chardo.Chartreux Blanc", "Un vin blanc sec, ample et aromatique. Idéal sur un foie gras ou un saumon sauce au beurre citron.<br><br>Dégustation : Robe jaune dorée ; Nez gourmand sur les fruits blancs mûrs et exotiques ; Bouche ample sur des notes de vanille, d’amandes grillées et de fleurs blanches (blanc sec).<br>Accord mets/vin : poisson en sauce, dessert, foie gras.<br><br>À la croisée des chemins entre Provence et Languedoc, entre Terres du Roy et Cité des Papes, les coopérateurs du Cellier des Chartreux partagent un héritage riche de cultures et d’histoires.<br>En 1612, après 25 ans de travaux, l’Étang de Pujaut, à quelques lieues de la Cité des Papes, est enfin asséché par les Chartreux. C’est en 1929, année de la création du Syndicat Général des Vignerons des Côtes du Rhône, le Cellier des Chartreux voit le jour, motivé par une volonté depuis respectée : créer des vins issus d’un savoir-faire transmis et renouvelé de génération en génération depuis 400 ans.", "12.5", "39", "2", "38", "33","2", "16", "cellier_des_chartreux_origine_blanc.png", "1"),
("00460", "Igp Gard Marselan Chartreux Rouge", "Un vin rouge léger et fruité. Parfait sur une entrecôte frites.<br><br>Dégustation : Robe rouge rubis ; Nez gourmand sur la fraise et la framboise ; Bouche fruitée et légèrement réglissée . <br>Accord mets/vin : viande rouge, charcuterie/fromage.<br><br>À la croisée des chemins entre Provence et Languedoc, entre Terres du Roy et Cité des Papes, les coopérateurs du Cellier des Chartreux partagent un héritage riche de cultures et d’histoires.<br>En 1612, après 25 ans de travaux, l’Étang de Pujaut, à quelques lieues de la Cité des Papes, est enfin asséché par les Chartreux. C’est en 1929, année de la création du Syndicat Général des Vignerons des Côtes du Rhône, le Cellier des Chartreux voit le jour, motivé par une volonté depuis respectée : créer des vins issus d’un savoir-faire transmis et renouvelé de génération en génération depuis 400 ans.", "7.35", "39", "1", "38", "33","8", "16", "cellier_des_chartreux_marselan_rouge.png", "1"),
("00461", "Igp Gard Origine Chartreux Rouge", "Un vin rouge généreux et épicé qui pourra accompagner une daube de sanglier.<br><br>Dégustation : Robe rouge profond ; Nez gourmand sur la cerise et la vanille ; Bouche structurée, fruitée et ample. <br>Accord mets/vin : gibier, viande en sauce.<br><br>À la croisée des chemins entre Provence et Languedoc, entre Terres du Roy et Cité des Papes, les coopérateurs du Cellier des Chartreux partagent un héritage riche de cultures et d’histoires.<br>En 1612, après 25 ans de travaux, l’Étang de Pujaut, à quelques lieues de la Cité des Papes, est enfin asséché par les Chartreux. C’est en 1929, année de la création du Syndicat Général des Vignerons des Côtes du Rhône, le Cellier des Chartreux voit le jour, motivé par une volonté depuis respectée : créer des vins issus d’un savoir-faire transmis et renouvelé de génération en génération depuis 400 ans.", "12.5", "39", "1", "38", "33","8", "16", "cellier_des_chartreux_origine_rouge.png", "1"),
("00462", "Igp Gard Viognier Chartreux Blanc", "Un vin blanc sec et aromatique, parfait pour vos apéritifs entre amis.<br><br>Dégustation : Robe jaune or pâle ; Nez floral et complexe légèrement vanillé, brioché ; Bouche ample sur des notes de fruits frais et de noisette.<br>Accord mets/vin : apéritif, poisson, dessert.<br><br>À la croisée des chemins entre Provence et Languedoc, entre Terres du Roy et Cité des Papes, les coopérateurs du Cellier des Chartreux partagent un héritage riche de cultures et d’histoires.<br>En 1612, après 25 ans de travaux, l’Étang de Pujaut, à quelques lieues de la Cité des Papes, est enfin asséché par les Chartreux. C’est en 1929, année de la création du Syndicat Général des Vignerons des Côtes du Rhône, le Cellier des Chartreux voit le jour, motivé par une volonté depuis respectée : créer des vins issus d’un savoir-faire transmis et renouvelé de génération en génération depuis 400 ans.", "7.9", "39", "2", "38", "33","2", "16", "cellier_des_chartreux_viognier_les_iles_blanches_blanc.png", "1"),
("00469", "Igp Var Domaine La Goujonne Lea Bio Blc", "Un vin blanc sec et fruité pour un bel apéritif entre amis.<br><br>Encépagement : assemblage de différents cépages phares de la Provence.<br>Dégustation : robe jaune pâle cristalline, nez aux notes de fruits blancs et jaune ; bouche ronde et équilibrée.<br>Accord mets/vin : apéritif, poissons grillés.<br><br>Le Domaine de la Goujonne situé à Tourves, est un domaine familiale créée en 1967. Sa situation géographique permet de proposer différentes appellations de Provence : IGP Var, Coteaux-Varois-En-Provence, Côtes de Provence.<br>Le domaine travaille en Agriculture Biologique.", "7.35", "14", "2", "43", "32","2", "16", "domaine_goujonne_lea_blanc.png", "1"),
("00474", "Igp Var Domaine La Goujonne Lea Bio Rose", "Un vin rosé sec et fruité pour un bel apéritif entre amis.<br><br>Encépagement : assemblage de différents cépages phares de la Provence.<br>Dégustation : robe rose pâle, nez de fruits rouges ; bouche fruitée et ronde. <br>Accord mets/vin : apéritif, grillades, salade d’été.<br><br>Le Domaine de la Goujonne situé à Tourves, est un domaine familiale créée en 1967. Sa situation géographique permet de proposer différentes appellations de Provence : IGP Var, Coteaux-Varois-En-Provence, Côtes de Provence.<br>Le domaine travaille en Agriculture Biologique.", "7.35", "14", "3", "43", "32","7", "16", "domaine_goujonne_lea_rose.png", "1"),
("00490", "Bouchon Verseur L901", "Accessoire permettant de facilité le service du vinaigre.", "6.95", "29", "10", "0", "9","0", "0", "bouchon_liege_vinaigrier.jpg", "1"),
("00494", "Corbieres Boutenac Fabre Gasparet Rouge", "Un Corbières puissant, généreux et boisé, idéal pour un carré d'agneau aux herbes de provence.<br><br>Encépagement : Syrah, Carignan, Mourvèdre élevé 12 mois en fût de chêne.<br><br>Dégustation : robe rubis foncé, nez épicé et fruité aux notes de cerises bien mûres, bouche ronde, ample, épicée avec un bel équilibre boisé et des tanins fins.<br>Accord mets/vin : viande en sauce, gibier, cassoulet.<br><br>Existe en Magnum et Jeroboam.<br><br>La Famille Fabre se passionne pour le vin depuis plus de 14 générations. Depuis 1605 les vignobles ont évolué par des alliances heureuses en restant toujours proches de la terre et de la vigne, aujourd’hui avec 5 châteaux dans le Languedoc. La Famille Fabre aime l’exigence du métier de vigneron et s’engage quotidiennement à révéler le meilleur de son terroir grâce à un travail de sélection parcellaire et une vraie maîtrise des techniques de culture, d’élevage et de vinification, toujours dans le respect de la Terre.", "17.25", "20", "1", "21", "24","8", "16", "fabre_corbiere_gasparet_boutenac_rouge.png", "1"),
("00495", "Corbieres Cht De Luc Jumelles Rouge", "Un Corbières rouge épicé sur des notes de fruits noirs. Idéal sur une côtes de boeuf grillée.<br><br>Encépagement : Syrah, Carignan, Mourvèdre, Grenache<br><br>Dégustation : Robe grenat ; Nez épicé et fruité aux notes de cassis et de mûres ; Bouche aux notes de fruits noirs bien mûrs, bel équilibre et tanins souples.<br>Accord mets/vin : viande rouge grillée, camembert braisés.<br><br>La Famille Fabre se passionne pour le vin depuis plus de 14 générations. Depuis 1605 les vignobles ont évolué par des alliances heureuses en restant toujours proches de la terre et de la vigne, aujourd’hui avec 5 châteaux dans le Languedoc. La Famille Fabre aime l’exigence du métier de vigneron et s’engage quotidiennement à révéler le meilleur de son terroir grâce à un travail de sélection parcellaire et une vraie maîtrise des techniques de culture, d’élevage et de vinification, toujours dans le respect de la Terre.", "10.2", "20", "1", "21", "24","8", "16", "fabre_corbieres_jumelles_rouge.png", "1"),
("00524", "Madiran Cht Peyros Vieilles Vignes Rouge", "Un Madiran Vieilles Vignes rouge charpenté et puissant. Idéal sur une épaule d'agneau mijotée au four.<br><br>Encépagement : Tannat, Cabernet franc.<br><br>Dégustation : Nez intense aux notes de fruits noirs. La bouche est puissante et ronde aux notes de fruits confits et une pointe de caramel. La finale est longue avec des tanins puissants mais velouté.<br>Accord mets/vin : Viande grillée, magret de canard ou gibier en sauce.<br><br>Le Château Peyros est situé dans la région du Vic Bilh et s'étend sur 20 hectares avec 70% de Tannat et 30% de Cabernet Franc. Il est le seul vignoble du Madiran a être exposé plein sud, cela permet donc à la vigne d'être mieux nourrie et de produire des vins plus qualitatifs. Fervents adeptes de la culture raisonnée, l'équipe de Peyros depuis l'arrivée de la Famille Lesgourgues a réduit de plus de 40% l'utilisation de produits de traitement.", "16", "28", "1", "48", "36","8", "16", "chateau_peyros_vieilles_vignes_rouge.png", "1"),
("00546", "Rasteau La Gardine Rouge Magnum Cb", "Un Rasteau puissant et généreux qui accompagnera à merveille un côtes de boeuf grillée.<br><br>Encépagement : Grenache, Syrah<br><br>Dégustation : Robe grenat aux reflets violets ; Nez fruité au notes de fruits noirs et d'épices douces ; Bouche élégante, généreuse et fruitée aux notes de mûres, de cassis et de cerises à clafoutis.<br>Accord mets/vin : viande grillée, une épaule d'agneau au thym.<br><br>Existe en 75cl et en Jeroboam.<br><br>La tradition viticole de la famille Brunel remonte au 17ème siècle. Gaston Brunel, un célèbre négociant, acheta le Château de la Gardine à Châteauneuf du Pape en 1945. Le domaine est maintenant tenu par ses deux fils, avec l’aide de leurs épouses et de leurs enfants. Le domaine s’étend sur 52 ha de vignes (48 ha de rouge et 4 ha de blanc).<br>La bouteille Gardine, originale et élégante, est née d'un heureuxhasard...<br>Lorsqu’il voulut agrandir sa cave pour la première fois, en creusant dans le sol, Gaston Brunel trouva une très vieille bouteille qui avait été soufflée à la bouche. La forme lui plut beaucoup, il décida donc d’embouteiller tout son vin dans une bouteille similaire. Au début il ne parvint pas à trouver un fabricant en France capable de reproduire cette bouteille, tant elle était particulière. Il les fit donc fabriquer en Italie. Le premier millésime dans la bouteille Gardine fut 1964. La bouteille est désormais fabriquée en France dans un moule créé spécifiquement pour la famille Brunel.", "66.5", "36", "1", "61", "33","8", "19", "gardine_rasteau_rouge.png", "1"),
("00547", "Ch9 Du Pape Tradition La Gardine Rouge", "Un Châteauneuf-du-Pape rouge généreux, puissant, fruité aillant un bon potentiel de garde. Idéal sur une daube provençale.<br><br>Encépagement : Grenache, Mourvèdre, Syrah, Muscardin élevés en fût de chêne<br><br>Dégustation : Robe pourpre ; Nez épicé aux notes de fruits noirs ; Bouche ample, fruitée et torréfiée aux notes de cacao avec des tannins soyeux.<br>Accord mets/vin : gibier en sauce, viandes grillées.<br><br>Existe en Magnum.<br><br>La tradition viticole de la famille Brunel remonte au 17ème siècle. Gaston Brunel, un célèbre négociant, acheta le Château de la Gardine à Châteauneuf du Pape en 1945. Le domaine est maintenant tenu par ses deux fils, avec l’aide de leurs épouses et de leurs enfants. Le domaine s’étend sur 52 ha de vignes (48 ha de rouge et 4 ha de blanc).<br>La bouteille Gardine, originale et élégante, est née d'un heureuxhasard...<br>Lorsqu’il voulut agrandir sa cave pour la première fois, en creusant dans le sol, Gaston Brunel trouva une très vieille bouteille qui avait été soufflée à la bouche. La forme lui plut beaucoup, il décida donc d’embouteiller tout son vin dans une bouteille similaire. Au début il ne parvint pas à trouver un fabricant en France capable de reproduire cette bouteille, tant elle était particulière. Il les fit donc fabriquer en Italie. Le premier millésime dans la bouteille Gardine fut 1964. La bouteille est désormais fabriquée en France dans un moule créé spécifiquement pour la famille Brunel.", "50.55", "36", "1", "17", "33","8", "16", "gardine_chateau_neuf_du_pape_tradition_rouge.png", "1"),
("00558", "Cot.Blaye Cht Loumelat Lesgourges Rge", "Un Côtes de Blaye épicé et fruité. Parfait sur des cotelettes d’agneau grillées.<br><br>Encépagement : Merlot, Cabernet sauvignon<br><br>Dégustation : Robe rouge sombre ; Nez boisé aux notes de fruits rouges ; Bouche souple, fruitée sur des tanins mûrs.<br>Accord mets/vin : viande grillée, fromages.<br><br>Vignoble le plus authentique de la région de Bordeaux et classé au patrimoine mondial de l'UNESCO.<br>Surplombant l'estuaire de la Gironde, on y cultive à flanc de coteaux les vignes qui profitent d'une exposition privilégiée. On y élabore des vins d'une finesse rare.<br>Propriété historique des Côtes de Blaye, le château produit la Cuvée J.J. Lesgourgues à forte personnalité qui exprime l’essence même de cette appellation.", "12", "28", "1", "6", "8","8", "16", "chateau_loumelat_cote_de_blaye_rouge.png", "1"),
("00562", "Igp Syrah Faury Hautes Ribaudes Rouge", "Un vin rouge léger et fruité. Idéal sur un plateau de charcuterie/fromage.<br><br>Encépagement : Syrah <br><br>Dégustation : Nez aux arômes de petits fruits rouges frais et légèrement épicé ; Bouche fruitée et gourmande.<br><br>Philippe Faury a créé ce domaine en 1979 à Chavanay, dans le hameau de la Ribaudy, Berceau de la famille. Son plus jeune fils, Lionel, s'est installé en 2005, après une expérience en Australie.<br><br>Domaine de 17Ha, Lionel Faury s'atèle à produire des vins qui expriment la diversité des terroirs en appellations Condrieu, Saint Joseph, Côte-Rôtie, et en IGP Rhodanienne.", "13.15", "30", "1", "0", "33","8", "16", "domaine_lionel_faury_hautes_ribaudes_rouge.png", "1"),
("00563", "St Joseph Les Ribaudes Lionel Faury Rge", "Un Saint Joseph rouge structuré et fruité qui accompagnera à merveille un rougail saucisses.<br><br>Encépagement : Syrah<br><br>Dégustation :  Le nez révèle des arômes intenses de fruits rouges frais tels que la framboise et la cerise, accompagnés de notes épicées et de légères nuances florales.<br>En bouche, ce vin se distingue par sa finesse et son équilibre. Les tanins sont présents, mais délicats, offrant une texture soyeuse. On retrouve les fruits rouges perçus au nez, ainsi que des touches d'épices douces et de réglisse, qui ajoutent de la complexité à l'ensemble.<br>Accord mets/vin : viandes grillées, fromages affinés, plats mijotés.<br><br>Existe en Magnum.<br><br>Philippe Faury a créé ce domaine en 1979 à Chavanay, dans le hameau de la Ribaudy, Berceau de la famille. Son plus jeune fils, Lionel, s'est installé en 2005, après une expérience en Australie.<br><br>Domaine de 17Ha, Lionel Faury s'atèle à produire des vins qui expriment la diversité des terroirs en appellations Condrieu, Saint Joseph, Côte-Rôtie, et en IGP Rhodanienne.", "23.35", "30", "1", "74", "33","8", "16", "domaine_lionel_faury_saint_joseph_ribaude_rouge.png", "1"),
("00564", "St Joseph Les Ribaudes Lionel Faury Blc", "Un Saint Joseph blanc ample et élégant. Parfait sur un risotto champignons.<br><br>Encépagement : Marsanne, Roussanne.<br><br>Dégustation : Robe brillante jaune or, Nez aux notes de fruits blancs (litchis, pêche) et un peu miêlé, Bouche ample et suave avec une finale persistante et bien équilibrée.<br>Accord mets/vin : viandes blanches en sauce, poissons gras (saumon, lotte)<br><br>Philippe Faury a créé ce domaine en 1979 à Chavanay, dans le hameau de la Ribaudy, Berceau de la famille. Son plus jeune fils, Lionel, s'est installé en 2005, après une expérience en Australie.<br><br>Domaine de 17Ha, Lionel Faury s'atèle à produire des vins qui expriment la diversité des terroirs en appellations Condrieu, Saint Joseph, Côte-Rôtie, et en IGP Rhodanienne.", "23.35", "30", "2", "74", "33","2", "16", "domaine_lionel_faury_saint_joseph_ribaude_blanc.png", "1"),
("00567", "Igp Gard La Nuit. Chats Sont Gris Rose", "Un vin rosé sec, floral et aromatique. Idéal pour vos apéritifs et grillades entre amis.<br><br>Dégustation : Robe rose pâle ; Nez complexe et intense aux notes de pêches blanches ; Bouche onctueuse et fraîche légèrement citronnée.<br>Accord mets/vin : apéritif, plats méditerranéens.<br><br>À la croisée des chemins entre Provence et Languedoc, entre Terres du Roy et Cité des Papes, les coopérateurs du Cellier des Chartreux partagent un héritage riche de cultures et d’histoires.<br>En 1612, après 25 ans de travaux, l’Étang de Pujaut, à quelques lieues de la Cité des Papes, est enfin asséché par les Chartreux. C’est en 1929, année de la création du Syndicat Général des Vignerons des Côtes du Rhône, le Cellier des Chartreux voit le jour, motivé par une volonté depuis respectée : créer des vins issus d’un savoir-faire transmis et renouvelé de génération en génération depuis 400 ans.", "6.1", "39", "3", "38", "33","7", "16", "cellier_des_chartreux_la_nuit_tous_les_chats_sont_gris_rose.png", "1"),
("00568", "Igp Gard Chamasutra Chartreux Rouge", "Un vin rouge léger et très fruité. Idéal sur un apéritif ou sur un plateau de charcuterie.<br><br>Dégustation : Robe rouge sombre ; Nez complexe et intense aux notes de fruits rouges ; Bouche souple, gourmande et légèrement vanillée/toastée<br>Accord mets/vin : apéritif, grillades, gibiers.<br><br>À la croisée des chemins entre Provence et Languedoc, entre Terres du Roy et Cité des Papes, les coopérateurs du Cellier des Chartreux partagent un héritage riche de cultures et d’histoires.<br>En 1612, après 25 ans de travaux, l’Étang de Pujaut, à quelques lieues de la Cité des Papes, est enfin asséché par les Chartreux. C’est en 1929, année de la création du Syndicat Général des Vignerons des Côtes du Rhône, le Cellier des Chartreux voit le jour, motivé par une volonté depuis respectée : créer des vins issus d’un savoir-faire transmis et renouvelé de génération en génération depuis 400 ans.", "6.1", "39", "1", "38", "33","8", "16", "cellier_des_chartreux_chamasutra_rouge.png", "1"),
("00569", "Igp Gard Je Donne Ma Langue Au Chat Blc", "Un vin blanc sec et fruité, parfait pour vos apéritifs entre amis.<br><br>Dégustation : Robe jaune or ; Nez gourmand sur le litchi et la mangue ; Bouche ronde sur des notes de fruits exotiques et de fruits jaune.<br>Accord mets/vin : apéritif, plat exotique.<br><br>À la croisée des chemins entre Provence et Languedoc, entre Terres du Roy et Cité des Papes, les coopérateurs du Cellier des Chartreux partagent un héritage riche de cultures et d’histoires.<br>En 1612, après 25 ans de travaux, l’Étang de Pujaut, à quelques lieues de la Cité des Papes, est enfin asséché par les Chartreux. C’est en 1929, année de la création du Syndicat Général des Vignerons des Côtes du Rhône, le Cellier des Chartreux voit le jour, motivé par une volonté depuis respectée : créer des vins issus d’un savoir-faire transmis et renouvelé de génération en génération depuis 400 ans.", "6.1", "39", "2", "38", "33","2", "16", "cellier_des_chartreux_je_donne_ma_langue_au_chat_blanc.png", "1"),
("00570", "Cdr Chevalier Anthelme Chartreux Rouge", "Un Côtes du Rhône rouge charnue et fruité. Idéal sur un plateau de charcuterie/fromage ou un onglet aux échalottes.<br><br>Dégustation : Robe rouge rubis ; Nez complexe aux notes de cerises confiturées et de réglisse ; Bouche ronde sur des arômes confits, épicés et poivrés.<br>Accord mets/vin : viande grillée, gibier, fromages.<br><br>À la croisée des chemins entre Provence et Languedoc, entre Terres du Roy et Cité des Papes, les coopérateurs du Cellier des Chartreux partagent un héritage riche de cultures et d’histoires.<br>En 1612, après 25 ans de travaux, l’Étang de Pujaut, à quelques lieues de la Cité des Papes, est enfin asséché par les Chartreux. C’est en 1929, année de la création du Syndicat Général des Vignerons des Côtes du Rhône, le Cellier des Chartreux voit le jour, motivé par une volonté depuis respectée : créer des vins issus d’un savoir-faire transmis et renouvelé de génération en génération depuis 400 ans.", "6.2", "39", "1", "26", "33","8", "16", "cellier_des_chartreux_chevalier_anthelme_rouge.png", "1"),
("00571", "Bib Igp Gard Sauvignon Chartreux Blc 5 L", "Un vin blanc sec aux notes citronnées, parfait pour une dorade au four.<br><br>Encépagement : Sauvignon<br><br>Dégustation : vif, frais aux arômes agrumes (citron, pamplemousse).<br>Accord mets/vin : viande blanche, poisson.<br><br>À la croisée des chemins entre Provence et Languedoc, entre Terres du Roy et Cité des Papes, les coopérateurs du Cellier des Chartreux partagent un héritage riche de cultures et d’histoires.<br>En 1612, après 25 ans de travaux, l’Étang de Pujaut, à quelques lieues de la Cité des Papes, est enfin asséché par les Chartreux. C’est en 1929, année de la création du Syndicat Général des Vignerons des Côtes du Rhône, le Cellier des Chartreux voit le jour, motivé par une volonté depuis respectée : créer des vins issus d’un savoir-faire transmis et renouvelé de génération en génération depuis 400 ans.", "24.5", "39", "4", "38", "33","2", "0", "bag_in_box_cellier_des_chartreux_sauvignon_blanc.png", "1"),
("00572", "Bib Igp Gard Merlot Chartreux Rouge 5 L", "Un vin rouge léger, rond et fruité. Idéal sur un plateau de charcuterie/fromage.<br><br>Encépagement : Merlot<br><br>Dégustation : gourmand, une belle rondeur et bonne finale. Arômes de cassis et réglisse.<br>Accord mets/vin : viande rouge, charcuterie/fromage.<br><br>À la croisée des chemins entre Provence et Languedoc, entre Terres du Roy et Cité des Papes, les coopérateurs du Cellier des Chartreux partagent un héritage riche de cultures et d’histoires.<br>En 1612, après 25 ans de travaux, l’Étang de Pujaut, à quelques lieues de la Cité des Papes, est enfin asséché par les Chartreux. C’est en 1929, année de la création du Syndicat Général des Vignerons des Côtes du Rhône, le Cellier des Chartreux voit le jour, motivé par une volonté depuis respectée : créer des vins issus d’un savoir-faire transmis et renouvelé de génération en génération depuis 400 ans.", "17.6", "39", "4", "38", "33","8", "0", "bag_in_box_cellier_des_chartreux_merlot_rouge.png", "1"),
("00573", "Bib Igp Gard Cab.Sauvignon Chart. Rge 5L", "Un vin rouge léger et fruité. Idéal sur une entrecôte frites.<br><br>Encépagement : Cabernet-Sauvignon<br><br>Dégustation : bonne structure, une grande finesse et bonne longueur en bouche. Arômes de cassis et réglisse.<br>Accord mets/vin : viande rouge, gibier.<br><br>À la croisée des chemins entre Provence et Languedoc, entre Terres du Roy et Cité des Papes, les coopérateurs du Cellier des Chartreux partagent un héritage riche de cultures et d’histoires.<br>En 1612, après 25 ans de travaux, l’Étang de Pujaut, à quelques lieues de la Cité des Papes, est enfin asséché par les Chartreux. C’est en 1929, année de la création du Syndicat Général des Vignerons des Côtes du Rhône, le Cellier des Chartreux voit le jour, motivé par une volonté depuis respectée : créer des vins issus d’un savoir-faire transmis et renouvelé de génération en génération depuis 400 ans.", "17.35", "39", "4", "38", "33","8", "0", "bag_in_box_cellier_des_chartreux_cabernet_sauvignon_rouge.png", "1"),
("00574", "Bib Cdr Chartreux Rouge 5 L", "Un Côtes du Rhône rouge fruité et épicé, parfait pour une côte de boeuf grillée.<br><br>Encépagement : Grenache, Syrah, Carignan, Cinsault, Mourvèdre<br><br>Dégustation : gourmand, souple et fruité. Arômes de fruits rouges confits et de poivre noir.<br>Accord mets/vin : viande rouge, charcuterie/fromage.<br><br>Existe en 10L.<br><br>À la croisée des chemins entre Provence et Languedoc, entre Terres du Roy et Cité des Papes, les coopérateurs du Cellier des Chartreux partagent un héritage riche de cultures et d’histoires.<br>En 1612, après 25 ans de travaux, l’Étang de Pujaut, à quelques lieues de la Cité des Papes, est enfin asséché par les Chartreux. C’est en 1929, année de la création du Syndicat Général des Vignerons des Côtes du Rhône, le Cellier des Chartreux voit le jour, motivé par une volonté depuis respectée : créer des vins issus d’un savoir-faire transmis et renouvelé de génération en génération depuis 400 ans.", "24.6", "39", "4", "26", "33","8", "0", "bag_in_box_cellier_des_chartreux_cotes_du_rhone_rouge.png", "1"),
("00575", "Bib Igp Gard Grenache Chartreux Rose 5 L", "Un vin rosé sec d'une belle complexité aromatique. Il accompagnera à merveille vos apéritifs et vos grillades estivales.<br><br>Encépagement : Grenache<br><br>Dégustation : sec, vif et frais aux notes de petits fruits rouge (fraise, framboise).<br>Accord mets/vin : apéritif, grillades, salade d’été.<br><br>Existe en 10L. <br><br>À la croisée des chemins entre Provence et Languedoc, entre Terres du Roy et Cité des Papes, les coopérateurs du Cellier des Chartreux partagent un héritage riche de cultures et d’histoires.<br>En 1612, après 25 ans de travaux, l’Étang de Pujaut, à quelques lieues de la Cité des Papes, est enfin asséché par les Chartreux. C’est en 1929, année de la création du Syndicat Général des Vignerons des Côtes du Rhône, le Cellier des Chartreux voit le jour, motivé par une volonté depuis respectée : créer des vins issus d’un savoir-faire transmis et renouvelé de génération en génération depuis 400 ans.", "18.7", "39", "4", "38", "33","7", "0", "bag_in_box_cellier_des_chartreux_grenache_rose.png", "1"),
("00576", "Bib Igp Oc Cuvee Chartreux Blanc 5 L", "Un vin blanc sec, floral et ample pour accompagner vos apéritifs ou une blanquette de veau.<br><br>Encépagement : Clairette, Bourboulenc, Grenache blanc, Marsanne, Roussanne.<br><br>Dégustation : vif, frais aux arômes floraux et de fruits frais.<br>Accord mets/vin : viande blanche, poisson.<br><br>Existe en 10L.<br><br>À la croisée des chemins entre Provence et Languedoc, entre Terres du Roy et Cité des Papes, les coopérateurs du Cellier des Chartreux partagent un héritage riche de cultures et d’histoires.<br>En 1612, après 25 ans de travaux, l’Étang de Pujaut, à quelques lieues de la Cité des Papes, est enfin asséché par les Chartreux. C’est en 1929, année de la création du Syndicat Général des Vignerons des Côtes du Rhône, le Cellier des Chartreux voit le jour, motivé par une volonté depuis respectée : créer des vins issus d’un savoir-faire transmis et renouvelé de génération en génération depuis 400 ans.", "18.7", "39", "4", "42", "24","2", "0", "bag_in_box_cellier_des_chartreux_igp_oc_blanc.png", "1"),
("00577", "Bib Cdr Chartreux Rouge 10 L", "Un Côtes du Rhône rouge fruité et épicé, parfait pour une côte de boeuf grillée.<br><br>Encépagement : Grenache, Syrah, Carignan, Cinsault, Mourvèdre<br><br>Dégustation : gourmand, souple et fruité. Arômes de fruits rouges confits et de poivre noir.<br>Accord mets/vin : viande rouge, charcuterie/fromage.<br><br>Existe en 5L.<br><br>À la croisée des chemins entre Provence et Languedoc, entre Terres du Roy et Cité des Papes, les coopérateurs du Cellier des Chartreux partagent un héritage riche de cultures et d’histoires.<br>En 1612, après 25 ans de travaux, l’Étang de Pujaut, à quelques lieues de la Cité des Papes, est enfin asséché par les Chartreux. C’est en 1929, année de la création du Syndicat Général des Vignerons des Côtes du Rhône, le Cellier des Chartreux voit le jour, motivé par une volonté depuis respectée : créer des vins issus d’un savoir-faire transmis et renouvelé de génération en génération depuis 400 ans.", "44", "39", "4", "26", "33","8", "0", "bag_in_box_cellier_des_chartreux_cotes_du_rhone_rouge.png", "1"),
("00578", "Bib Igp Gard Grenache Chart. Rose 10 L", "Un vin rosé sec d'une belle complexité aromatique. Il accompagnera à merveille vos apéritifs et vos grillades estivales.<br><br>Encépagement : Grenache<br><br>Dégustation : sec, vif et frais aux notes de petits fruits rouge (fraise, framboise).<br>Accord mets/vin : apéritif, grillades, salade d’été.<br><br>Existe en 5L. <br><br>À la croisée des chemins entre Provence et Languedoc, entre Terres du Roy et Cité des Papes, les coopérateurs du Cellier des Chartreux partagent un héritage riche de cultures et d’histoires.<br>En 1612, après 25 ans de travaux, l’Étang de Pujaut, à quelques lieues de la Cité des Papes, est enfin asséché par les Chartreux. C’est en 1929, année de la création du Syndicat Général des Vignerons des Côtes du Rhône, le Cellier des Chartreux voit le jour, motivé par une volonté depuis respectée : créer des vins issus d’un savoir-faire transmis et renouvelé de génération en génération depuis 400 ans.", "34.4", "39", "4", "38", "33","7", "0", "bag_in_box_cellier_des_chartreux_grenache_rose.png", "1"),
("00579", "Bib Igp Oc Cuvee Chartreux Blanc 10 L", "Un vin blanc sec, floral et ample pour accompagner vos apéritifs ou une blanquette de veau.<br><br>Encépagement : Clairette, Bourboulenc, Grenache blanc, Marsanne, Roussanne.<br><br>Dégustation : vif, frais aux arômes floraux et de fruits frais.<br>Accord mets/vin : viande blanche, poisson.<br><br>Existe en 5L.<br><br>À la croisée des chemins entre Provence et Languedoc, entre Terres du Roy et Cité des Papes, les coopérateurs du Cellier des Chartreux partagent un héritage riche de cultures et d’histoires.<br>En 1612, après 25 ans de travaux, l’Étang de Pujaut, à quelques lieues de la Cité des Papes, est enfin asséché par les Chartreux. C’est en 1929, année de la création du Syndicat Général des Vignerons des Côtes du Rhône, le Cellier des Chartreux voit le jour, motivé par une volonté depuis respectée : créer des vins issus d’un savoir-faire transmis et renouvelé de génération en génération depuis 400 ans.", "34.3", "39", "4", "42", "24","2", "0", "bag_in_box_cellier_des_chartreux_igp_oc_blanc.png", "1"),
("00596", "Cdr Village St Gervais Syrius Blanc", "Un Côtes du Rhône blanc sec, rond et aromatique. Idéal en apéritif ou sur une dorade grillée au four.<br><br>Encépagement : Roussanne, Viognier.<br><br>Dégustation : Robe jaune or ; Nez complexe aux notes d’agrumes et de fruits secs ; Bouche aromatique et tendue.<br>Accord mets/vin : apéritif, viande blanche, poisson.<br><br>Existe en Magnum.<br><br>Le domaine est situé à Saint-Gervais, petit village aux accents typiques du midi qui fait partie des dix-huit dénominations Côtes du Rhône Villages des vignobles de la Vallée du Rhône. Porte d’entrée de la vallée de la Cèze, bercé de mistral, son climat méditerranéen subit l’influence des Cévennes qui apportent de la fraicheur à ses vins.<br>Descendante d’une famille qui travaille la vigne depuis 1640, Claire Clavel, Femme Vigneronne, est épaulée par son père Denis. Elle conduit les 80 hectares de son domaine.", "11.9", "13", "2", "29", "33","2", "16", "domaine_clavel_syrius_blanc.png", "1"),
("00597", "Cdr Regulus Clavel Rouge Magnum", "Un Côtes du Rhône rouge léger et fruité. Idéal sur un plateau de charcuterie/fromages.<br><br>Encépagement : Grenache, Syrah et Marselan.<br><br>Dégustation : Robe rouge carmin, Nez puissant et charmeur aux notes de fruits rouges et de cacao, Bouche souple et ronde avec des tannins soyeux.<br>Accord mets/vin : apéritif, viande grillée.<br><br>Existe en 75cl.<br><br>Le domaine est situé à Saint-Gervais, petit village aux accents typiques du midi qui fait partie des dix-huit dénominations Côtes du Rhône Villages des vignobles de la Vallée du Rhône. Porte d’entrée de la vallée de la Cèze, bercé de mistral, son climat méditerranéen subit l’influence des Cévennes qui apportent de la fraicheur à ses vins.<br><br>Descendante d’une famille qui travaille la vigne depuis 1640, Claire Clavel, Femme Vigneronne, est épaulée par son père Denis. Elle conduit les 80 hectares de son domaine.", "20.8", "13", "1", "26", "33","8", "16", "domaine_clavel_regulus_rouge.png", "1"),
("00598", "Cdr Vill.Chusclan Cordelia Rouge Magnum", "Un Côtes du Rhône Villages rouge charnue et épicé. Idéal sur une côtes de boeuf.<br><br>Encépagement : Grenache, Syrah.<br><br>Dégustation : Robe rouge carmin, Nez puissant et complexe aux notes de garrigue et de fruit mûrs, Bouche souple et ronde avec des tannins soyeux.<br>Accord mets/vin : viande grillée, fromage de brebis.<br><br>Existe en 75cl.<br><br>Le domaine est situé à Saint-Gervais, petit village aux accents typiques du midi qui fait partie des dix-huit dénominations Côtes du Rhône Villages des vignobles de la Vallée du Rhône. Porte d’entrée de la vallée de la Cèze, bercé de mistral, son climat méditerranéen subit l’influence des Cévennes qui apportent de la fraicheur à ses vins.<br><br>Descendante d’une famille qui travaille la vigne depuis 1640, Claire Clavel, Femme Vigneronne, est épaulée par son père Denis. Elle conduit les 80 hectares de son domaine.", "25.15", "13", "1", "28", "33","8", "16", "domaine_clavel_cordelia_rouge.png", "1"),
("00599", "Cdr Vill.St Gervais Syrius Rouge Magnum", "Un Côtes du Rhône rouge épicé et fruité. Idéal sur des cotelletes d'agneau grillées.<br><br>Encépagement : Grenache, Syrah.<br><br>Dégustation : Robe rouge carmin ; Nez complexe aux notes de fruits rouges confiturés et d’épices ; Bouche riche, ample avec des tanins soyeux.<br>Accord mets/vin : gibier, viande rouge grillée.<br><br>Existe en 75cl et Jeroboam.<br><br>Le domaine est situé à Saint-Gervais, petit village aux accents typiques du midi qui fait partie des dix-huit dénominations Côtes du Rhône Villages des vignobles de la Vallée du Rhône. Porte d’entrée de la vallée de la Cèze, bercé de mistral, son climat méditerranéen subit l’influence des Cévennes qui apportent de la fraicheur à ses vins.<br>Descendante d’une famille qui travaille la vigne depuis 1640, Claire Clavel, Femme Vigneronne, est épaulée par son père Denis. Elle conduit les 80 hectares de son domaine.", "25.15", "13", "1", "29", "33","8", "16", "domaine_clavel_syrius_rouge.png", "1"),
("00600", "Cdr Vill.St Ger Clair De Lune Rge Magnum", "Un Côtes du Rhône rouge élégant et charnue. Idéal sur un faux filet sauce aux cèpes.<br><br>Encépagement : Grenache, Syrah et Mourvèdre<br><br>Dégustation : Robe rouge grenat, Nez puissant et intense aux notes de fruits noirs, d'épices et de cuir, Bouche ample et ronde, bien équilibré sur une finale aux notes réglisées.<br>Accord mets/vin : viande épicée, en sauce.<br><br>Existe en 75cl et Jeroboam.<br><br>Le domaine est situé à Saint-Gervais, petit village aux accents typiques du midi qui fait partie des dix-huit dénominations Côtes du Rhône Villages des vignobles de la Vallée du Rhône. Porte d’entrée de la vallée de la Cèze, bercé de mistral, son climat méditerranéen subit l’influence des Cévennes qui apportent de la fraicheur à ses vins.<br><br>Descendante d’une famille qui travaille la vigne depuis 1640, Claire Clavel, Femme Vigneronne, est épaulée par son père Denis. Elle conduit les 80 hectares de son domaine.", "33.5", "13", "1", "29", "33","8", "16", "domaine_clavel_clair_de_lune_rouge.png", "1"),
("00601", "Cdr Vill.St Gerv. Syrius Rouge Jeroboam", "Un Côtes du Rhône rouge épicé et fruité. Idéal sur des cotelletes d'agneau grillées.<br><br>Encépagement : Grenache, Syrah.<br><br>Dégustation : Robe rouge carmin ; Nez complexe aux notes de fruits rouges confiturés et d’épices ; Bouche riche, ample avec des tanins soyeux.<br>Accord mets/vin : gibier, viande rouge grillée.<br><br>Existe en Magnum et 75cl.<br><br>Le domaine est situé à Saint-Gervais, petit village aux accents typiques du midi qui fait partie des dix-huit dénominations Côtes du Rhône Villages des vignobles de la Vallée du Rhône. Porte d’entrée de la vallée de la Cèze, bercé de mistral, son climat méditerranéen subit l’influence des Cévennes qui apportent de la fraicheur à ses vins.<br>Descendante d’une famille qui travaille la vigne depuis 1640, Claire Clavel, Femme Vigneronne, est épaulée par son père Denis. Elle conduit les 80 hectares de son domaine.", "55.1", "13", "1", "29", "33","8", "16", "domaine_clavel_syrius_rouge.png", "1"),
("00602", "Chorey-Les-Beaune Jl Maldant Blanc", "Un Chorey-les-beaune blanc beurré et brioché, parfait sur une blanquette de veau.<br><br>Encépagement : Chardonnay<br><br>Dégustation : robe jaune or, nez noisette et fruits blancs, fleurs blanches, bouche ronde avec une belle acidité donnant une finale persistante.<br>Accord mets/vin : poisson en sauce, jambon de Serrano, fromage.<br><br>Existe en Magnum.<br><br>Domaine familial depuis des années, le Domaine Maldant-Pauvelot s’étend sur 18 Ha sur les Grands Terroirs de Bourgogne. Jean-Luc Maldant reprends les reines en 2009 et installe le domaine dans la qualité de ses produits.", "25.9", "23", "2", "19", "10","2", "16", "jean_luc_maldant_chorey_les_beaune_blanc.png", "1"),
("00603", "Bg Aligote Jl Maldant Blanc", "Un Bourgogne aligoté sec et aromatique, idéal pour raclette ou une tartiflette.<br><br>Encépagement : Aligoté<br><br>Dégustation : robe jaune pâle, nez aromatique aux notes d’agrumes et de pierre à fusil, bouche légèrement iodée, fraîche.<br>Accord mets/vin : plateau de fruits de mer, poisson.<br><br>Existe en Magnum.<br><br>Domaine familial depuis des années, le Domaine Maldant-Pauvelot s’étend sur 18 Ha sur les Grands Terroirs de Bourgogne. Jean-Luc Maldant reprends les reines en 2009 et installe le domaine dans la qualité de ses produits.", "12.95", "23", "2", "8", "10","2", "16", "jean_luc_maldant_aligote_blanc.png", "1"),
("00604", "Bg Chardonnay A.Geoffroy Blanc", "Un Bourgogne Chardonnay complexe et minérale pour accompagner un plateau de fromage.<br><br>Encépagement : Chardonnay<br><br>Dégustation : Robe or pâle ; Nez jeune et frais aux notes d’agrumes et d’acacia ; Bouche minérale aux notes de fleurs, de citron et de pain frais.<br>Accord mets/vin : crustacés, poissons fins, fromage affiné.<br><br>Le Domaine Alain Geoffroy installé à Beine, près de Chablis, réalise la production, la récolte et la vente de vins blancs issus de cépages Chardonnay.<br>Honoré Geoffroy, vers 1850, étendit l’exploitation sur les communes de Beine et la Chapelle Vaupelteigne, au coeur du vignoble chablisien.<br>Alain Geoffroy fit passer la propriété au niveau de Domaine. Digne héritier de toutes les générations, il a su lui conserver le caractère familial et traditionnel.<br>L’objectif est produire des vins de Chablis tout en finesse et en élégance dans le respect du terroir chablisien.", "17.75", "1", "2", "9", "10","2", "16", "alain_geoffroy_bourgogne_chardonnay_blanc.png", "1"),
("00605", "Chablis Domaine Verger A.Geoffroy Blanc", "Un Chablis rond et minéral qui pourra s'accorder sur un dorade en papillotte.<br><br>Encépagement : Chardonnay<br><br>Dégustation : Robe or blanc ; Nez riche et mûr aux notes florales et fruits blanc ; Bouche ample, minérale et fruitée.<br>Accord mets/vin : crustacés, poissons fins, fromage affiné.<br><br>Le Domaine Alain Geoffroy installé à Beine, près de Chablis, réalise la production, la récolte et la vente de vins blancs issus de cépages Chardonnay.<br>Honoré Geoffroy, vers 1850, étendit l’exploitation sur les communes de Beine et la Chapelle Vaupelteigne, au coeur du vignoble chablisien.<br>Alain Geoffroy fit passer la propriété au niveau de Domaine. Digne héritier de toutes les générations, il a su lui conserver le caractère familial et traditionnel.<br>L’objectif est produire des vins de Chablis tout en finesse et en élégance dans le respect du terroir chablisien.", "22.45", "1", "2", "14", "10","2", "16", "alain_geoffroy_chablis_domaine_le_verger_blanc.png", "1"),
("00606", "Chablis 1Er Cru Beauroy A.Geoffroy Blanc", "Un Chablis 1er Cru minéral et floral. Idéal pour accompagner une moules/frites.<br><br>Encépagement : Chardonnay<br><br>Dégustation : Robe or blanc ; Nez floral aux notes de fruits frais ; Bouche ample, ronde, minérale et pleine de fraîcheur.<br>Accord mets/vin : crustacés, poissons fins, fromage affiné.<br><br>Le Domaine Alain Geoffroy installé à Beine, près de Chablis, réalise la production, la récolte et la vente de vins blancs issus de cépages Chardonnay.<br>Honoré Geoffroy, vers 1850, étendit l’exploitation sur les communes de Beine et la Chapelle Vaupelteigne, au coeur du vignoble chablisien.<br>Alain Geoffroy fit passer la propriété au niveau de Domaine. Digne héritier de toutes les générations, il a su lui conserver le caractère familial et traditionnel.<br>L’objectif est produire des vins de Chablis tout en finesse et en élégance dans le respect du terroir chablisien.", "30.65", "1", "2", "14", "10","2", "16", "alain_geoffroy_chablis_premier_cru_beauroy_blanc.png", "1"),
("00611", "Corbieres Cht De Luc Jumelles Blanc", "Un Corbières blanc sec d'une belle complexité aromatique, parfait un plateau de fromage frais.<br><br>Encépagement : Grenache blanc, Roussanne.<br><br>Dégustation : Robe jaune pâle ; Nez au notes de noisette et de chèvrefeuille ; Bouche ample et complexe aux notes de fleurs blanches.<br>Accord mets/vin : tartes aux légumes, lasagnes de chèvre frais.<br><br>La Famille Fabre se passionne pour le vin depuis plus de 14 générations. Depuis 1605 les vignobles ont évolué par des alliances heureuses en restant toujours proches de la terre et de la vigne, aujourd’hui avec 5 châteaux dans le Languedoc. La Famille Fabre aime l’exigence du métier de vigneron et s’engage quotidiennement à révéler le meilleur de son terroir grâce à un travail de sélection parcellaire et une vraie maîtrise des techniques de culture, d’élevage et de vinification, toujours dans le respect de la Terre.", "10.2", "20", "2", "21", "24","2", "16", "fabre_corbieres_jumelles_blanc.png", "1"),
("00615", "Igp Oc Tempranillo Grde Courtade Rouge", "Un vin rouge léger et fruité pour accompagner vos plateau de charcuterie et fromage.<br><br>Encépagement : Tempranillo.<br><br>Dégustation : Robe grenat ; Nez fruité aux notes de violette ; Bouche ronde avec des tanins souples aux notes de fruits noirs et d’épices.<br>Accord mets/vin : viande rouge grillée, charcuterie/fromage.<br><br>La Famille Fabre se passionne pour le vin depuis plus de 14 générations. Depuis 1605 les vignobles ont évolué par des alliances heureuses en restant toujours proches de la terre et de la vigne, aujourd’hui avec 5 châteaux dans le Languedoc. La Famille Fabre aime l’exigence du métier de vigneron et s’engage quotidiennement à révéler le meilleur de son terroir grâce à un travail de sélection parcellaire et une vraie maîtrise des techniques de culture, d’élevage et de vinification, toujours dans le respect de la Terre.", "9.65", "20", "1", "42", "24","8", "16", "fabre_tempranillo_rouge.png", "1"),
("00616", "Igp Oc Syrah Viognier Dom.Luc Rouge", "Un vin rouge léger et fruité pour accompagner vos apéritifs et plateau de charcuterie/fromages.<br><br>Encépagement : Syrah, Viognier.<br><br>Dégustation : Robe rubis ; Nez fruité aux notes de fruits noirs ; Bouche ronde avec des tanins fondus.<br>Accord mets/vin : viande rouge grillée, charcuterie/fromage.<br><br>La Famille Fabre se passionne pour le vin depuis plus de 14 générations. Depuis 1605 les vignobles ont évolué par des alliances heureuses en restant toujours proches de la terre et de la vigne, aujourd’hui avec 5 châteaux dans le Languedoc. La Famille Fabre aime l’exigence du métier de vigneron et s’engage quotidiennement à révéler le meilleur de son terroir grâce à un travail de sélection parcellaire et une vraie maîtrise des techniques de culture, d’élevage et de vinification, toujours dans le respect de la Terre.", "9.05", "20", "1", "42", "24","8", "16", "fabre_luc_syrah_viognier_rouge.png", "1"),
("00617", "Biere Alaryk Blanche 33 Cl", "Cuvée'Blanche' : mousse légère, floral et fruité aux notes d'agrumes, de banane et très légèrement épicée.<br>Degré : 4,5°<br><br>Pays : France (Hérault)<br><br>Existe en 75cl.<br><br>La Brasserie Alaryk est situé à Bézier et a été créée par Sébastien Alary. <br>Les bières sont fabriquées en Agriculture Biologique, sont non pasteurisées, non filtrées et sans ajout de CO2.", "4.55", "20", "8", "0", "7","3", "8", "bieres_artisanales_alaryk.png", "1"),
